HAL_MessageProcess_List.HAL_MessageProcess:
TX_login [handle=none]() {
	declare	recv	Node;
	declare	mNum	mNum;

	recv = ALL;
	loopAll drivers[driver] {
		mNum = getNewMsgNum();
		driver>>send(login(recv,mNum));
	}
return 0;
}

RX_login [handle=login]() {
	declare	sender	Node;
	declare	driver	Driver;
	declare	mNum	mNum;

	sender = getSender();
	driver = getDriver();
	mNum = getMsgNum();

	if (!addNode(sender, driver)) {
		return driver>>send(login(sender,mNum));
	}
	return 0;
}

TX_run [handle=none](Node dest) {
	declare	driver	Driver;
	declare	sender	Node;
	declare	mNum	mNum;

	sender = getSelfSenderID();
	if (dest != ALL) {
		mNum = getNewMsgNum();
		driver = getDriver(dest);
		driver>>send(run(sender,mNum));
		return waitACK(mNum);
	}

	loopAll drivers[drv] {
		mNum = getNewMsgNum();
		drv>>send(run(sender,mNum));
		if (waitACK(mNum)) {
			return -1;
		}
	}
	return 0;
}

RX_run [handle=run]() {
	declare	sender			Node;
	declare	driver			Driver;
	declare	mNum			mNum;

	sender = getSender();
	driver = getDriver();
	mNum = getMsgNum();

	if (!runAP()) {
		return driver>>send(ACK(sender,mNum));
	} else {
		return driver>>send(NACK(sender,mNum));
	}
}

TX_logout [handle=none]() {
	declare	recv	Node;
	declare	mNum	mNum;

	recv = ALL;
	loopAll drivers[driver] {
		mNum = getNewMsgNum();
		driver>>send(logout(recv,mNum));
	}
return 0;
}

RX_logout [handle=logout]() {
	declare	sender	Node;
	
	sender = getSender();

	removeNode(sender);
	return 0;
}

TX_updateVariable [handle=none](VarIndex vi) {
	declare	mNum	mNum;
	declare	apV		Variable;

	apV = getVariableByIndex(vi);

	loopAll nodes[node] {
		if (getNodeIDfromNode(node) != getSelfSenderID()) {
			mNum = getNewMsgNum();
			apV>>call(sendUpdate(getVariableData(apV),getDriverFromNode(node),ALL,mNum,vi));
			if (waitACK(mNum)) {
				return -1;
			}
		}
	}
	return 0;
}

RX_updateVariable [handle=updateVariable]() {
	declare	sender			Node;
	declare	driver			Driver;
	declare	mNum			mNum;
	declare	apV				Variable;
	declare	gIndex			int;

	sender = getSender();
	driver = getDriver();
	mNum = getMsgNum();
	gIndex = getMsgDataToInt(0);

	apV = getVariableByIndex(gIndex);
	if (!apV) {
		return -1;
	}

	apV>>call(recvUpdate(getVariableData(apV),getMsgDataRef()));

	return driver>>send(ACK(sender,mNum));
}

Code_List.Code:
// ==============================================
// generic vector interface for integer and
// rational types based on the std. C lib
// ==============================================

// ----------------------------------------------
// generic integer type
// ----------------------------------------------

// transforms the integer to a 'unique form'
inline uint32_t APgenericInteger_ToUniform (uint32_t v) {
	uint32_t u;
	u = v & 0xFF;
	u <<= 8;
	v >>= 8;
	u |= (v & 0xFF);
	u <<= 8;
	v >>= 8;
	u |= (v & 0xFF);
	u <<= 8;
	v >>= 8;
	u |= (v & 0xFF);
	return u;
}

// inverse transformation of the unique integer to the 'local form'
inline uint32_t APgenericInteger_FromUniform (uint32_t v) {
	uint32_t u;
	u = v & 0xFF;
	u <<= 8;
	v >>= 8;
	u |= v & 0xFF;
	u <<= 8;
	v >>= 8;
	u |= v & 0xFF;
	u <<= 8;
	v >>= 8;
	u |= v & 0xFF;
	return u;
}



// type for a generic vector based int type
typedef struct SAPgenericIntegerVector {
	int32_t *	pVal;
	int			num;
} TAPgenericIntegerVector;

// create int vector
TAPgenericIntegerVector * APgenericIntegerVector_create (int num) {
	int i;
	// alloc memory
	TAPgenericIntegerVector * pI = malloc (sizeof(TAPgenericIntegerVector));
	if (!pI) return NULL;
	pI->pVal = malloc (sizeof(int32_t)*num);
	if (!pI->pVal) {
		free (pI);
		return NULL;
	}
	pI->num = num;
	// set memory
	for (i = 0; i< num; i++) {
		pI->pVal[i] = 0;
	}
	return pI;
}

// destroy int vector
void APgenericIntegerVector_destroy (TAPgenericIntegerVector * pV) {
	if (pV) {
		free(pV->pVal);
		free(pV);
	}
}

// resize int vector
int APgenericIntegerVector_resize (TAPgenericIntegerVector * pV, int newNum) {
	int i;

	if (pV->num == newNum) return 0;
	free (pV->pVal);
	pV->pVal = malloc (sizeof(int32_t)*newNum);
	if (!pV->pVal) return -1;
	pV->num = newNum;
	// set memory
	for (i = 0; i< newNum; i++) {
		pV->pVal[i] = 0;
	}
	return 0;
}

// assign a = values
int APgenericIntegerVector_assignConst (TAPgenericIntegerVector * pa, int iStart, int num, int32_t * pVals) {
	int i = iStart;
	int imax = i + num;
	int32_t * pD = pa->pVal + iStart;
	if (imax > pa->num) imax = pa->num;

	for (; i < imax; i++) {
		*pD = *pVals;
		pD++;
		pVals++;
	}
	return 0;
}

// assign a = b
int APgenericIntegerVector_assign (TAPgenericIntegerVector * pa, TAPgenericIntegerVector * pb) {
	int imax = pa->num;
	int i;
	for (i = 0; i < imax; i++) {
		pa->pVal[i] = pb->pVal[i];
	}
	return 0;
}


// add c = a + b
int APgenericIntegerVector_add (TAPgenericIntegerVector * pa, TAPgenericIntegerVector * pb, TAPgenericIntegerVector * pc) {
	int imax = pc->num;
	int i;
	for (i = 0; i < imax; i++) {
		pc->pVal[i] = pa->pVal[i] + pb->pVal[i];
	}
	return 0;
}

// sub c = a - b
int APgenericIntegerVector_sub (TAPgenericIntegerVector * pa, TAPgenericIntegerVector * pb, TAPgenericIntegerVector * pc) {
	int imax = pc->num;
	int i;
	for (i = 0; i < imax; i++) {
		pc->pVal[i] = pa->pVal[i] - pb->pVal[i];
	}
	return 0;
}

// mul c = a * b
int APgenericIntegerVector_mul (TAPgenericIntegerVector * pa, TAPgenericIntegerVector * pb, TAPgenericIntegerVector * pc) {
	int imax = pc->num;
	int i;
	for (i = 0; i < imax; i++) {
		pc->pVal[i] = pa->pVal[i] * pb->pVal[i];
	}
	return 0;
}

// div c = a * b
int APgenericIntegerVector_div (TAPgenericIntegerVector * pa, TAPgenericIntegerVector * pb, TAPgenericIntegerVector * pc) {
	int imax = pc->num;
	int i;
	for (i = 0; i < imax; i++) {
		pc->pVal[i] = pa->pVal[i] / pb->pVal[i];
	}
	return 0;
}

// modulo c = a mod b
int APgenericIntegerVector_mod (TAPgenericIntegerVector * pa, TAPgenericIntegerVector * pb, TAPgenericIntegerVector * pc) {
	int imax = pc->num;
	int i;
	for (i = 0; i < imax; i++) {
		pc->pVal[i] = pa->pVal[i] % pb->pVal[i];
	}
	return 0;
}

// cmp Element cmp(a[i],b[j]):
//		res = -1: a < b
//		res = 1 : a > b
//		res = 0: a = b
int APgenericIntegerVector_cmpEle (TAPgenericIntegerVector * pa, TAPgenericIntegerVector * pb, int ia, int ib) {
	int zwv = pa->pVal[ia] - pb->pVal[ib];
	if (!zwv) return 0;
	if (zwv < 0) return -1;
	return 1;
}


// ----------------------------------------------
// generic rational type
// ----------------------------------------------

// type for a generic vector based float type
typedef struct SAPgenericRationalVector {
	float *		pVal;
	int			num;
} TAPgenericRationalVector;

// create float vector
TAPgenericRationalVector * APgenericRationalVector_create (int num) {
	int i;
	// alloc memory
	TAPgenericRationalVector * pR = malloc (sizeof(TAPgenericRationalVector));
	if (!pR) return NULL;
	pR->pVal = malloc (sizeof(float)*num);
	if (!pR->pVal) {
		free (pR);
		return NULL;
	}
	pR->num = num;
	// set memory
	for (i = 0; i< num; i++) {
		pR->pVal[i] = 0.f;
	}
	return pR;
}

// destroy float vector
void APgenericRationalVector_destroy (TAPgenericRationalVector * pV) {
	if (pV) {
		free(pV->pVal);
		free(pV);
	}
}

// resize float vector
int APgenericRationalVector_resize (TAPgenericRationalVector * pV, int newNum) {
	int i;

	if (pV->num == newNum) return 0;
	free (pV->pVal);
	pV->pVal = malloc (sizeof(float)*newNum);
	if (!pV->pVal) return -1;
	pV->num = newNum;
	// set memory
	for (i = 0; i< newNum; i++) {
		pV->pVal[i] = 0.f;
	}
	return 0;
}

// assign a = values
int APgenericRationalVector_assignConst (TAPgenericRationalVector * pa, int iStart, int num, float * pVals) {
	int i = iStart;
	int imax = i + num;
	float * pD = pa->pVal + iStart;
	if (imax > pa->num) imax = pa->num;

	for (; i < imax; i++) {
		*pD = *pVals;
		pD++;
		pVals++;
	}
	return 0;
}

// assign a = b
int APgenericRationalVector_assign (TAPgenericRationalVector * pa, TAPgenericRationalVector * pb) {
	int imax = pa->num;
	int i;
	for (i = 0; i < imax; i++) {
		pa->pVal[i] = pb->pVal[i];
	}
	return 0;
}


// add c = a + b
int APgenericRationalVector_add (TAPgenericRationalVector * pa, TAPgenericRationalVector * pb, TAPgenericRationalVector * pc) {
	int imax = pc->num;
	int i;
	for (i = 0; i < imax; i++) {
		pc->pVal[i] = pa->pVal[i] + pb->pVal[i];
	}
	return 0;
}

// sub c = a - b
int APgenericRationalVector_sub (TAPgenericRationalVector * pa, TAPgenericRationalVector * pb, TAPgenericRationalVector * pc) {
	int imax = pc->num;
	int i;
	for (i = 0; i < imax; i++) {
		pc->pVal[i] = pa->pVal[i] - pb->pVal[i];
	}
	return 0;
}

// mul c = a * b
int APgenericRationalVector_mul (TAPgenericRationalVector * pa, TAPgenericRationalVector * pb, TAPgenericRationalVector * pc) {
	int imax = pc->num;
	int i;
	for (i = 0; i < imax; i++) {
		pc->pVal[i] = pa->pVal[i] * pb->pVal[i];
	}
	return 0;
}

// div c = a * b
int APgenericRationalVector_div (TAPgenericRationalVector * pa, TAPgenericRationalVector * pb, TAPgenericRationalVector * pc) {
	int imax = pc->num;
	int i;
	for (i = 0; i < imax; i++) {
		pc->pVal[i] = pa->pVal[i] / pb->pVal[i];
	}
	return 0;
}

// modulo c = a mod b
int APgenericRationalVector_mod (TAPgenericRationalVector * pa, TAPgenericRationalVector * pb, TAPgenericRationalVector * pc) {
	int imax = pc->num;
	int i;
	for (i = 0; i < imax; i++) {
		pc->pVal[i] = fmodf(pa->pVal[i],pb->pVal[i]);
	}
	return 0;
}

// cmp Element cmp(a[i],b[j]):
//		res = -1: a < b
//		res = 1 : a > b
//		res = 0: a = b
int APgenericRationalVector_cmpEle (TAPgenericRationalVector * pa, TAPgenericRationalVector * pb, int ia, int ib) {
	float zwv = pa->pVal[ia] - pb->pVal[ib];
	if (zwv == 0.f) return 0;
	if (zwv < 0.f) return -1;
	return 1;
}

