void HALfunc_ID10_assignRational(void * pIPcpu, uAPInterpreterFunctionParameter * pParams) {
// parameter a descr: the destination
	void ** pa = &( pParams[0].fp_pD);
// parameter b descr: the source
	void ** pb = &( pParams[1].fp_pD);
};

