	TAPrealMMUMemory * pMnext;

// 1. delete al mmu entry's
	// 1.1 unused entry
	pM = pMMU->pUnusedList;
	while (pM) {
		pMnext = pM->pNext;
		MemoryEntry_delete(pM);
		pM = pMnext;
	}
	pMMU->pUnusedList = NULL;
	// 1.2 used blocks
	pM = pMMU->pStart;
