// endian check
int is_big_endian(void) {
    union {
        uint32_t i;
        char c[4];
    } bint = {0x01020304};

    return bint.c[0] == 1;
}

// loads the alc from a file
int loadALCfromFile (const char * szFilname,int * pEndianness, int32_t *pVarcount, int32_t * pInstrCount, int32_t ** ppVarList, int32_t ** ppInstructions) {
	FILE *		f;
	int32_t		instrSize;

	*ppInstructions = NULL;
	*ppVarList = NULL;
	*pVarcount = 0;
	*pInstrCount = 0;

	f = fopen (szFilname,"rb");

	if (!f) return -1;

	uint8_t	idField[4];
	uint8_t endian, sysEndian;;

	if (1 != fread (&idField,sizeof(int8_t)*4,1,f)) goto error;
	if (1 != fread (&endian,sizeof(int8_t),1,f)) goto error;

	if (1 != fread (pVarcount,sizeof(int32_t),1,f)) goto error;
	if (1 != fread (pInstrCount,sizeof(int32_t),1,f)) goto error;
	if (1 != fread (&instrSize,sizeof(int32_t),1,f)) goto error;


	if (!is_big_endian()) sysEndian = eAP_littleEndian;
		else sysEndian = eAP_bigEndian;
	if (endian != sysEndian) {
		APendianConversation32Bit((uint32_t *)pVarcount);
		APendianConversation32Bit((uint32_t *)pInstrCount);
		APendianConversation32Bit((uint32_t *)&instrSize);
	}

	// check if the size of the instructions saved at the file is equal to the of the engine
	if (((instrSize)-1) != dAPInterpreterFuncMaxParams) {
		goto error;
	}


	*ppVarList = malloc ((sizeof(int32_t)*(dAPInterpreterVariableMaxParams))*(*pVarcount));
	if (!*ppVarList) goto error;

	*ppInstructions = malloc (sizeof(int32_t)*(dAPInterpreterFuncMaxParams+1) *(*pInstrCount));
	if (!*ppInstructions) goto error;

	if (*pVarcount != fread (*ppVarList,sizeof(int32_t)*(dAPInterpreterVariableMaxParams),*pVarcount,f)) goto error;

	if (*pInstrCount != fread (*ppInstructions,sizeof(int32_t)*(dAPInterpreterFuncMaxParams+1),*pInstrCount,f)) goto error;

	fclose (f);

	*pEndianness = endian;
	return 0;

error:
	fclose (f);
	if (*ppInstructions) {
		free (*ppInstructions);
		*ppInstructions = NULL;
	}

	if (*ppVarList) {
		free (*ppVarList);
		*ppVarList = NULL;
	}
	*pVarcount = 0;
	*pInstrCount = 0;
	*pEndianness = 0;
	return -2;
}

// loads the ALC - stored in memory - into the AP
int loadProgrammToTheAP (
		TAPInterpreter	IP,
		int				endianness,
		int32_t 		varCount,
		int32_t			instrCount,
		int32_t *		pVarList,
		int32_t *		pInstructions
		) {
	APInterpreterClean (IP);
	APInterpreterSetState (IP,endianness,eAPInterpreterState_loadProgramm);
	APInterpreterInitNewProgramm (IP,endianness,instrCount,varCount);

	int32_t vc = varCount;
	int32_t ic = instrCount;
	int sysEndian;
	if (!is_big_endian()) sysEndian = eAP_littleEndian;
		else sysEndian = eAP_bigEndian;
	if (endianness != sysEndian) {
		APendianConversation32Bit((uint32_t *)&vc);
		APendianConversation32Bit((uint32_t *)&ic);
	}

	int32_t i;
	typedef int32_t TBinVarData[2];
	TBinVarData * pV = (TBinVarData *) pVarList;
	for (i = 0; i < vc;i++) {
		if (APInterpreterLoadVar (IP,endianness,i,(*pV)[0],(*pV)[1])) return -10;
		pV++;
	}
	int32_t * pInstr = pInstructions;
	for (i = 0; i < ic;i++) {
		if (APInterpreterLoadInstr (IP,endianness,i,pInstr)) return -20;
		pInstr += (dAPInterpreterFuncMaxParams + 1);
	}
	return 0;
}
#endif // of dAPuseFileIO


#ifdef dAPuseExternalAPCode

// loads the ALC - stored in memory - into the AP
int loadProgrammToTheAP (TAP * pAP) {

	extern int const gALCdumpEndian;
	extern int const gALCdumpVarCount;
	extern int const gALCdumpInstrCount;
	extern int const gALCdumpInstrSize;

	// a constant global variable holding the values for the variables
	extern const int32_t gcVars[];
	// a constant global variable holding the values for the instructions
	extern const int32_t gcInstructions[];

	if (gALCdumpInstrSize != (dAPInterpreterFuncMaxParams + 1)) return -1;

	APInterpreterClean (pAP->IP);
	APInterpreterSetState (pAP->IP,gALCdumpEndian,eAPInterpreterState_loadProgramm);
	APInterpreterInitNewProgramm (pAP->IP,gALCdumpEndian,(int32_t)gALCdumpInstrCount,(int32_t)gALCdumpVarCount);

	int32_t i;
	typedef int32_t TBinVarData[2];
	TBinVarData const * pV = (TBinVarData const *) gcVars;
	for (i = 0; i < gALCdumpVarCount;i++) {
		if (APInterpreterLoadVar (pAP->IP,gALCdumpEndian,i,(*pV)[0],(*pV)[1])) return -10;
		pV++;
	}

	// if the values at the flash or rom we need to make a copy at the RAM to use the AP API functions
	int32_t const * pInstr = gcInstructions;
	int32_t instrCopy[dAPInterpreterFuncMaxParams + 1];
	for (i = 0; i < gALCdumpInstrCount;i++) {
		// copy instruction from the const buffer to an non const buffer
		memcpy (instrCopy, pInstr, sizeof(uint32_t)*(dAPInterpreterFuncMaxParams + 1));
		// load instruction
		if (APInterpreterLoadInstr (pAP->IP,gALCdumpEndian,i,instrCopy)) return -20;
		pInstr += (dAPInterpreterFuncMaxParams + 1);
	}
	return 0;
}


#endif

TAPMsgDrv 	gDrvL [1];
TAP 		AP;

void * APIPthread(void * pArgs) {
	APInterpreterProcessState(AP.IP);

	return NULL;
}

void * APMSthread (void * pArgs) {
	APMessageProcessingThread (&AP);
	return NULL;
}

int main()
{
	WSADATA			wsaData;
	pthread_t		IPthread;
	pthread_t		MSthread;


	int rc;
	int endianS = eAP_littleEndian;

    if ((WSAStartup(MAKEWORD(2, 2), &wsaData))) {
    	printf("WSAStartup failed!");
    	return -1;
    }

	APdrviver_1_init(&gDrvL[0]);

	time_t t;

	time(&t);
	srand((unsigned int)t);              /* Zufallsgenerator initialisieren */

	int uuid = rand();

	printf ("AP uuid %i\n",uuid);

	// start up gtk+
	APgtk_start ();

	//rc = APinit(&AP,815,gDrvL,1,1000,eAP_littleEndian);
	rc = APinit(&AP,(TAPNodeID)uuid,gDrvL,1,1000,endianS);
	if (rc) {
		printf("audio processor init failed with error code %i!",rc);
		goto exit;
	}


#ifdef dAPuseFileIO
	int32_t varC;
	int32_t instrC;

	int32_t * varL;
	int32_t * instrL;
	int endian;

	rc = loadALCfromFile ("../APgen/ALASM/crossOver5Way.alc",&endian,&varC,&instrC,&varL,&instrL);
	if (rc) goto exit;
	rc = loadProgrammToTheAP (AP.IP,endian,varC,instrC,varL,instrL);
#endif

#ifdef dAPuseExternalAPCode
	rc = loadProgrammToTheAP (&AP);
	if (rc) {
		printf("load programm failed with %i", rc);
		goto exit;
	}
#endif

#ifdef dAPuseFileIO
	free (varL);
	free (instrL);
#endif

	rc = APInterpreterSetState (AP.IP,endianS,eAPInterpreterState_run);

	pthread_create(&IPthread,NULL,&APIPthread,NULL);
	pthread_create(&MSthread,NULL,&APMSthread,NULL);

	printf("press a key to exit");
	getch();

	rc = APInterpreterSetState (AP.IP,endianS,eAPInterpreterState_halt);


exit:
	APdelete(&AP);

	WSACleanup();

	APgtk_end();

	return EXIT_SUCCESS;
}

// ===============================
// variable implementation for a string(var id = 3)
// ===============================
// --------------------------------------
// TAPstring
// --------------------------------------

// AP string type
typedef struct SAPstring {
	char * 		szTxt;	//!< pointer to the vector of chars
	int 		len;	//!< length of the string
} TAPstring;

// creates a AP string
int APstring_create (
		TAPstring * 	pStr,	//!<(in) pointer to the string structure witch should be filled
		int 			len		//!<(in) length of the string
	) {
	(pStr->szTxt) = malloc ((len+1)*sizeof(char));
	if (!pStr->szTxt) return -1;
	*(pStr->szTxt) = 0;
	pStr->len = len;
	return 0;
}

// frees a AP string
void APstring_free (
		TAPstring * 	pStr	//!<(in) pointer to the string
	) {
	free ((pStr->szTxt));
	pStr->szTxt = NULL;
	pStr->len = 0;
}

// resizes the string
int APstring_resize (
		TAPstring * 	pStr,	//!<(in) pointer to the string
		int				newLen	//!<(in) new length of the string
	) {
	char * pNS = realloc(pStr->szTxt,(newLen+1));
	if (!pNS) return -1;
	pStr->szTxt = pNS;
	pStr->len = newLen;
	return 0;
}

// fills the string from an extern source
void APstring_fill(
		TAPstring * 	pStr,	//!<(in) pointer to the string
		int				iStart,	//!<(in) start index at the string
		int				imax,	//!<(in) end index of the string
		char *			pSource	//!<(in) source from where the chars are copied
	) {
	int i;
	char * pDest = pStr->szTxt +  iStart;
	// clip str
	if (imax > pStr->len) imax = pStr->len - iStart;

	for (i = 0;i < imax; i++) {
		*pDest = *pSource;
		pDest++;
		pSource++;
	}
	*pDest = 0;
}

// --------------------------------------
// TAPstringVector
// --------------------------------------

// vector of strings type
typedef struct SAPstringVector {
	TAPstring *		pStr;	//!< pointer to the first string at the vector
	unsigned int	num;	//!< amount of strings at the vector
} TAPstringVector;

// creates a AP string
TAPstringVector * APstringVector_create (
		int		num		//!<(in) number of strings at the vector
	) {
	TAPstringVector * pR;
	int i;

	pR = malloc(sizeof(TAPstringVector));
	if (!pR) return NULL;
	pR->pStr = malloc(sizeof(TAPstring)*num);
	if (!pR->pStr) {
		free (pR);
		return NULL;
	}
	// init str
	TAPstring * pS = pR->pStr;
	for (i = 0; i < num; i++) {
		pS->szTxt = NULL;
		pS->len = 0;
		pS++;
	}
	pR->num = num;
	return pR;
}

// frees a AP string
void APstringVector_free (
		TAPstringVector * 	pSV	//!<(in) pointer to the string vector
	) {
	unsigned int i;
	TAPstring * pS = pSV->pStr;

	for (i = 0; i < pSV->num; i++) {
		APstring_free(pS);
		pS++;
	}
	free(pSV->pStr);
	free(pSV);
}

