// ------------------------------
// drv own functions
// ------------------------------

// struct for the drv data
typedef struct SADSPuartDrv {
	TAPMsgHeader				h;	//!< transmit header template
	TAPReceiveStateMachine		rxSM;		//!< receive state machine
} TADSPuartDrv;

TADSPuartDrv gADSPuartDrv;

// ------------------------------
// drv own functions
// ------------------------------

void drv_2_sendHeader (
		uint32_t msgId,
		uint32_t recv,
		uint32_t num,
		uint32_t length
	) {
	gADSPuartDrv.h[eAPMsgHeaderPosition_msgTypeID] = msgId;
	gADSPuartDrv.h[eAPMsgHeaderPosition_msgNumber] = num;
	gADSPuartDrv.h[eAPMsgHeaderPosition_receiver] = recv;
	gADSPuartDrv.h[eAPMsgHeaderPosition_length] = length;
	sendUARTuint32Values(gADSPuartDrv.h,sizeof(TAPMsgHeader)/sizeof(uint32_t));
}

// call back function of the uart driver
int drv_2_cbAPClient (unsigned int d) {
	return gADSPuartDrv.rxSM.state (&(gADSPuartDrv.rxSM), &d, 1);
}

//open an existing driver and bind it to the AP
int drv_2_open (void * pAP, struct SAPMsgDrv *pDrv) {
	// init header
	gADSPuartDrv.h[eAPMsgHeaderPosition_endian] = ((TAP *) pAP)->sysEndian;
	gADSPuartDrv.h[eAPMsgHeaderPosition_sender] = ((TAP *) pAP)->nodeID;
	gADSPuartDrv.h[eAPMsgHeaderPosition_receiver] = dAPNodeID_ALL;
	gADSPuartDrv.h[eAPMsgHeaderPosition_msgTypeID] = 0;
	gADSPuartDrv.h[eAPMsgHeaderPosition_msgNumber] = 0;
	gADSPuartDrv.h[eAPMsgHeaderPosition_length] = 0;

	// save driver data
	pDrv->pDrvData = &gADSPuartDrv;

	// init receive state machine
	APInitReceiveStateMachine(
			&gADSPuartDrv.rxSM,
			((TAP *)pAP)->MS,
			pDrv
		);
	return 0;
}

//close the driver
int drv_2_close (struct SAPMsgDrv *pDrv) {
	return 0;
}
//destroys the driver
int drv_2_destroy (struct SAPMsgDrv *pDrv) {
	return 0;
}


//send raw data
int drv_2_sendRaw (struct SAPMsgDrv *pDrv, int amount, void *pData) {
	sendUARTuint32Values(pData,amount);
	return 0;
}


//acknowledge
int drv_2_ACK (void *pDrvData, uint32_t receiver, uint32_t mNum) {
	drv_2_sendHeader(eAPMsgTypes_ACK, receiver, mNum, 0);
	return 0;
}
//not acknowledge
int drv_2_NACK (void *pDrvData, uint32_t receiver, uint32_t mNum) {
	drv_2_sendHeader(eAPMsgTypes_NACK, receiver, mNum, 0);
	return 0;
}
//start sending a program
int drv_2_startPrg (void *pDrvData, uint32_t receiver, uint32_t mNum, int32_t globalVariableNumber, int32_t localVariableNumber, int32_t instructionNumber) {
	drv_2_sendHeader(eAPMsgTypes_startPrg, receiver, mNum, 0);
	sendUARTuint32Values((uint32_t *)&globalVariableNumber,1);
	sendUARTuint32Values((uint32_t *)&localVariableNumber,1);
	sendUARTuint32Values((uint32_t *)&instructionNumber,1);
	return 0;
}

//sends a variable
int drv_2_sendVariable (void *pDrvData, uint32_t receiver, uint32_t mNum, int32_t index, int32_t varTypeID, int32_t num) {
	drv_2_sendHeader(eAPMsgTypes_sendVariable, receiver, mNum, 3);
	sendUARTuint32Values((uint32_t *)&index,1);
	sendUARTuint32Values((uint32_t *)&varTypeID,1);
	sendUARTuint32Values((uint32_t *)&num,1);
	return 0;
}
//sends an instruction
int drv_2_sendInstruction (void *pDrvData, uint32_t receiver, uint32_t mNum, int32_t index, uint32_t * fbc) {
	drv_2_sendHeader(eAPMsgTypes_sendInstruction, receiver, mNum, dAPInterpreterFuncMaxParams + 1);
	sendUARTuint32Values((uint32_t *)&index,1);
	sendUARTuint32Values(fbc,sizeof(int32_t)*dAPInterpreterFuncMaxParams);
	return 0;
}
//sings that the program transmission has completed
int drv_2_endPrg (void *pDrvData, uint32_t receiver, uint32_t mNum) {
	drv_2_sendHeader(eAPMsgTypes_endPrg, receiver, mNum, 0);
	return 0;
}
//stops the AP
int drv_2_stop (void *pDrvData, uint32_t receiver, uint32_t mNum) {
	drv_2_sendHeader(eAPMsgTypes_stop, receiver, mNum, 0);
	return 0;
}
//the AP executes one instruction
int drv_2_step (void *pDrvData, uint32_t receiver, uint32_t mNum) {
	drv_2_sendHeader(eAPMsgTypes_step, receiver, mNum, 0);
	return 0;
}
//the AP runs the program
int drv_2_run (void *pDrvData, uint32_t receiver, uint32_t mNum) {
	drv_2_sendHeader(eAPMsgTypes_run, receiver, mNum, 0);
	return 0;
}
//a variable going to be updated
int drv_2_updateVariable (void *pDrvData, uint32_t receiver, uint32_t mNum, int32_t gIndex, int32_t dataElements) {
	drv_2_sendHeader(eAPMsgTypes_updateVariable, receiver, mNum, dataElements + 1);
	sendUARTuint32Values((uint32_t *)&gIndex,1);
	return 0;
}
//a AP is going to be logged in to the system
int drv_2_login (void *pDrvData, uint32_t receiver, uint32_t mNum) {
	drv_2_sendHeader(eAPMsgTypes_login, receiver, mNum, 0);
	return 0;
}
//a AP is going to be logged out of the system
int drv_2_logout (void *pDrvData, uint32_t receiver, uint32_t mNum) {
	drv_2_sendHeader(eAPMsgTypes_logout, receiver, mNum, 0);
	return 0;
}

// ===============================
// AP uuid = 12
// ===============================

// inits the AP
int APinit (
			TAP *				pAP,
			TAPNodeID			nodeID,
			const TAPMsgDrv *	pDrvList,
			const int			driverNumber,
			size_t				messagePoolSize,
			int					sysEndian
		)
{
	pAP->nodeID = nodeID;
	pAP->pNodeList = NULL;
	pAP->pDrvList = pDrvList;
	pAP->sysEndian = sysEndian;
	pAP->driverNumber = driverNumber;
	pAP->msgSysMMU = AP_MMU_create(messagePoolSize);
	pAP->IP = APInterpreterCreate(pAP);
	pAP->MS = APMScreate (pAP->msgSysMMU,sysEndian);
	pAP->msgNumber = 0;
	pAP->APstate = eAPstate_idle;

	if(
		(!pAP->msgSysMMU)||
		(!pAP->IP)||
		(!pAP->MS)
		) return -1;

	initHW();

	startHW();

	// login the ap to the message system
	return TX_login(pAP);
}

// deletes the AP
void APdelete (TAP * pAP)
{
	APMSdelete (pAP->MS);
	APInterpreterDelete(pAP->IP);
	AP_MMU_delete(pAP->msgSysMMU);
}

// find a node at the list
TAPNode * APfindNode(TAP * pAP, TAPNodeID nodeID) {
	TAPNode * pN = pAP->pNodeList;
	while (pN) {
		if (pN->nodeID == nodeID) return pN;
		pN = pN->pNext;
	};
	return NULL;
}

// adds a new node to the node list
int APaddNode(TAP * pAP, TAPNodeID newNodeID, const TAPMsgDrv * pDrv) {
	if (APfindNode(pAP,newNodeID)) return 1;
	TAPNode * pN = (TAPNode *) malloc(sizeof(TAPNode));
	if (!pN) return -1;
	pN->nodeID = newNodeID;
	pN->pDrv = pDrv;
	pN->pNext = pAP->pNodeList;
	pAP->pNodeList = pN;
	return 0;
}

// removes a node from the node list
void APremoveNode(TAP * pAP, TAPNodeID nodeID){
	TAPNode * pAntN = pAP->pNodeList; // antecessor node
	TAPNode * pActN = pAP->pNodeList; // actual node

	while (pActN) {
		// compare node id's
		if (pActN->nodeID == nodeID) {
			// unchain

			// check if we at the first position at the list
			if (pAP->pNodeList == pAntN) {
				// reset the pointer
				pAP->pNodeList = pActN->pNext;
			} else {
				// set the antecessor
				pAntN->pNext = pActN->pNext;
			}
			// free node
			free(pActN);
			// and abort
			return;
		}
		// the actual element becomes the precessor element
		pAntN = pActN;
		pActN = pActN->pNext;
	}
}

// get a new message number
unsigned int APgetNewMessageNumber (TAP *pAP) {
	pAP->msgNumber++;
	return pAP->msgNumber;
}

// find the driver associated with der nodeID
const TAPMsgDrv * APfindDrvBySenderID (TAP * pAP, TAPNodeID node) {
	TAPNode * pN = pAP->pNodeList;
	while (pN) {
		if (pN->nodeID == node) {
			return pN->pDrv;
		}
		pN = pN->pNext;
	}
	return NULL;
}

// runs the AP
int APrun(TAP *pAP) {
	pAP->APstate = eAPstate_run;
	return 0;
}


typedef struct SAPrealMMUMemory {
	int * 						pData;			// the data
	size_t						count;			// amount of data elements
	struct SAPrealMMUMemory *	pNext;			// next element
	struct SAPrealMMUMemory *	pPrev;			// previous element
} TAPrealMMUMemory;

//the mmu type
typedef struct SAPrealMMU {
	int * 					memory;				// the memory bolck
	TAPrealMMUMemory *		pStart;				// first element
	TAPrealMMUMemory *		pEnd;				// second element
	TAPrealMMUMemory *		pUnusedList;		// list with the unused elements
	int * 					pUnusedData;		// pointer to the unused memory
	size_t					elementsAvailable; 	// amount of elements witch are available without using the garbage collector
	size_t					totalAvailable;		// total amount of free bytes

} TAPrealMMU;

// ========================================
// memory entry functions
// ========================================

// a little macro for unchaining an element
#define DMemoryEntryUnchain(pM) \
	if (pM->pNext) pM->pNext->pPrev = pM->pPrev; \
	if (pM->pPrev) pM->pPrev->pNext = pM->pNext


//creates an memory entry
TAPrealMMUMemory * MemoryEntry_create () {
	TAPrealMMUMemory * pM = NULL;

	pM = (TAPrealMMUMemory *) malloc(sizeof(TAPrealMMUMemory));
	if (!pM) return NULL;
	pM->pData = NULL;
	pM->count = 0;
	pM->pNext = NULL;
	pM->pPrev = NULL;

	return pM;
}

//deletes an memory Entry
void MemoryEntry_delete (
		TAPrealMMUMemory * pM	// the memory to delete
		)
{
	// put the entry out of the chain
	DMemoryEntryUnchain(pM);
	// now we delete it
	free(pM);
}

// ========================================
// mmu helper
// ========================================

//alloc if needed a new memory entry
TAPrealMMUMemory * MMU_helper_createMemoryEntry (
		TAPrealMMU *	pMMU 		// MMU structure to init
		)
{
	// check if we have to alloc a new memory entry
	if (!pMMU->pUnusedList) return MemoryEntry_create();
	// no there is some left at the list
	TAPrealMMUMemory * pM;
	// take the first one
	pM = pMMU->pUnusedList;
	// reset the list
	pMMU->pUnusedList = pM->pNext;
	// now unchain the element (for sure)
	DMemoryEntryUnchain(pM);
	// set the element pointers
	pM->pNext = NULL;
	pM->pPrev = NULL;
	return pM;
}

//the garbage collector
void MMU_helper_garbageCollector (
		TAPrealMMU *	pMMU 		// MMU structure to init
		)
{
	TAPrealMMUMemory * pM = pMMU->pStart;
	int * pD = pMMU->memory;
	while (pM) {
		// check if we have to move the data
		if (pD != pM->pData) {
			// move the data
			memmove(pD,pM->pData,pM->count*sizeof(int));
		}
		// reset the destination pointer
		pD += pM->count;
		pM = pM->pNext;
	}
	// compressing memory finished
	// set the mmu vars new
	pMMU->elementsAvailable = pMMU->totalAvailable;
	pMMU->pUnusedData = pD;
}



// create a mmu
TAPMMU AP_MMU_create (size_t elementsNumber) {
	TAPrealMMU * pMMU;

	pMMU = (TAPrealMMU *) malloc (sizeof(TAPrealMMU));
	if (!pMMU) return NULL;



	// setup lists
	pMMU->pStart = NULL;
	pMMU->pEnd = NULL;
	pMMU->pUnusedList = NULL;

	pMMU->elementsAvailable =elementsNumber;
	pMMU->pUnusedData = (int *) malloc (pMMU->elementsAvailable);
	pMMU->totalAvailable = pMMU->elementsAvailable;
	return pMMU;
}

// destroying the mmu
void AP_MMU_delete (TAPMMU mmu) {
	TAPrealMMU * pMMU = (TAPrealMMU *) mmu;

	TAPrealMMUMemory * pM;
	TAPrealMMUMemory * pMnext;

// 1. delete al mmu entry's
	// 1.1 unused entry
	pM = pMMU->pUnusedList;
	while (pM) {
		pMnext = pM->pNext;
		MemoryEntry_delete(pM);
		pM = pMnext;
	}
	pMMU->pUnusedList = NULL;
	// 1.2 used blocks
	pM = pMMU->pStart;
	while (pM) {
		pMnext = pM->pNext;
		MemoryEntry_delete(pM);
		pM = pMnext;
	}
	pMMU->pStart = NULL;
	pMMU->pEnd = NULL;
// 2. delete mmu memory
	free (pMMU->memory);


}
// getting memmory from the mmu
TAPMMUmemmory AP_MMU_get (TAPMMU mmu, size_t elements) {
	TAPrealMMU * pMMU = (TAPrealMMU *) mmu;

	// check if there is enough space
	if (pMMU->totalAvailable < elements) goto error;
	// check if we have to use the garbage collector
	if (pMMU->elementsAvailable < elements) {
		// start garbage collector
		MMU_helper_garbageCollector(pMMU);
	}
	// we have enough memory so let's allocate some

	// get a new entry
	TAPrealMMUMemory * pM;
	pM = MMU_helper_createMemoryEntry(pMMU);
	if (!pM) return NULL;
	// get some memory
	pM->pData = pMMU->pUnusedData;
	pM->count = elements;
	// refresh data
	pMMU->pUnusedData += elements;
	pMMU->totalAvailable -= elements;
	pMMU->elementsAvailable -= elements;
	// insert memory element at the end of the list and update last element
	pM->pPrev = pMMU->pEnd;
	if (pMMU->pEnd) pMMU->pEnd->pNext = pM;
	if (!pMMU->pStart) pMMU->pStart = pM;
	pMMU->pEnd = pM;

	return pM;
error:

	return NULL;
}

// free memmory from the mmu
void AP_MMU_free (TAPMMU mmu, TAPMMUmemmory memory) {
	TAPrealMMU * pMMU = (TAPrealMMU *) mmu;
	TAPrealMMUMemory * pM = (TAPrealMMUMemory *) memory;

	if (!pM) return;
	// set mmu settings
	if (pMMU->pStart == pM) pMMU->pStart = pM->pNext;
	if (pMMU->pEnd == pM) pMMU->pEnd = pM->pPrev;
	// unchain element
	DMemoryEntryUnchain (pM);
	// and put it to the chain of unused
	pM->pNext = pMMU->pUnusedList;
	pM->pPrev = NULL;
	if (pMMU->pUnusedList) {
		pMMU->pUnusedList->pPrev = pM;
	}
	pMMU->pUnusedList = pM;
	// now set the mmu data new
	pMMU->totalAvailable += pM->count;

}

// getting access to the MMU data
void * AP_MMU_getData (TAPMMUmemmory memory) {
	TAPrealMMUMemory * pM = (TAPrealMMUMemory *) memory;
	return pM->pData;
}

// the real interpreter
typedef struct SAPrealInterpreter {
	TAP *						pAP;					// pointer to the audio processor
	int							state;					// state of the IP
	int							nextState;				// the next state of the IP
	TAPInterpreterCPU			cpu;					// the IP core
	TAPInterpreterFuncCall *	code;					// the code
	int32_t						instructionCount;		// number of instructions at the code
	TAPInterpreterVariable *	variables;				// the variables
	int32_t						variableCount;			// number of the variables
} TAPrealInterpreter;

// create a new interpreter
TAPInterpreter APInterpreterCreate (void * pAP) {
	TAPrealInterpreter * pIP = NULL;
	pIP = (TAPrealInterpreter *) malloc (sizeof(TAPrealInterpreter));
	if (!pIP) return NULL;

	pIP->pAP = pAP;
	pIP->state = eAPInterpreterState_idle;
	pIP->nextState = eAPInterpreterState_idle;
	pIP->cpu.IP = pIP;
	pIP->cpu.CF = 0;
	pIP->cpu.EF = 0;
	pIP->cpu.pCodeStart = NULL;
	pIP->cpu.pCodeEnd = NULL;
	pIP->cpu.pIP = NULL;

	pIP->code = NULL;
	pIP->instructionCount = 0;

	pIP->variables = NULL;
	pIP->variableCount = 0;

	return pIP;
}

// cleans the interpreter
void APInterpreterClean (TAPInterpreter IP) {
	TAPrealInterpreter * pIP = (TAPrealInterpreter *) IP;

	// clean code
	if (pIP->code) {
		free (pIP->code);
		pIP->code = NULL;
	}
	pIP->instructionCount = 0;

	// clean variables
	TAPInterpreterVariable * pV = pIP->variables;
	int i;
	for (i = 0; i < pIP->variableCount; i++) {
		if (pV->pVI) pV->pVI->pFkt_delete(pV->pData);
		pV++;
	}
	if (pIP->variables) {
		free (pIP->variables);
		pIP->variables = NULL;
	}
	pIP->variableCount = 0;

}

// deletes the interpreter
void APInterpreterDelete (TAPInterpreter IP) {
	TAPrealInterpreter * pIP = (TAPrealInterpreter *) IP;
	APInterpreterClean(IP);
	free (pIP);
}

int APInterpreterStateRun(TAPInterpreter IP) {
	TAPrealInterpreter * pIP = (TAPrealInterpreter *) IP;
	TAPInterpreterFuncCall * pFC;

	// setup cpu
	pIP->cpu.CF = 0;
	pIP->cpu.EF = 0;
	pIP->cpu.pIP = pIP->code;
	pIP->cpu.pCodeStart = pIP->code;
	pIP->cpu.pCodeEnd = pIP->code + pIP->instructionCount;

	// run code
	while (eAPInterpreterState_run == pIP->state) {
		pFC = pIP->cpu.pIP;
		// check if we reached the end of the code
		if (pFC > pIP->cpu.pCodeEnd) {
			return 0;
		}
		// execute command
		pFC->pHALFkt (&(pIP->cpu), pFC->param);
		// check error flags
		if (pIP->cpu.EF) {
			return -1;
		}
	}
	return 1;
}


// process the actual state
int APInterpreterProcessState(TAPInterpreter IP){
	TAPrealInterpreter * pIP = (TAPrealInterpreter *) IP;
	pIP->state = pIP->nextState;
	int rc = 0;

	switch (pIP->state) {
		case eAPInterpreterState_idle:
			break;
		case eAPInterpreterState_loadProgramm:
			break;
		case eAPInterpreterState_run:
			rc = APInterpreterStateRun(IP);
			if (rc >= 0) pIP->state = eAPInterpreterState_idle;
			break;
		case eAPInterpreterState_oneStep:
			break;
		case eAPInterpreterState_halt:
			break;
		default:
			return -10;
	}
	return rc;
}


// set interpreter state
int APInterpreterSetState (TAPInterpreter IP, int msgEndian, int32_t state) {
	TAPrealInterpreter * pIP = (TAPrealInterpreter *) IP;
	if (msgEndian != pIP->pAP->sysEndian) {
		APendianConversation32Bit((uint32_t *)&state);
	}
	pIP->nextState = (int) state;
	return 0;
}

// setup the interpreter for a new program
int APInterpreterInitNewProgramm (TAPInterpreter IP, int msgEndian, int32_t instructionsNumber, int32_t VariableNumber) {
	TAPrealInterpreter * pIP = (TAPrealInterpreter *) IP;
	int i;

	APInterpreterClean (IP);

	if (msgEndian != pIP->pAP->sysEndian) {
		APendianConversation32Bit((uint32_t *)&instructionsNumber);
		APendianConversation32Bit((uint32_t *)&VariableNumber);
	}

	pIP->code = (TAPInterpreterFuncCall *) malloc(sizeof(TAPInterpreterFuncCall)*instructionsNumber);
	pIP->instructionCount = instructionsNumber;

	pIP->variables = (TAPInterpreterVariable *) malloc(sizeof(TAPInterpreterVariable) * (VariableNumber));
	for (i = 0; i < VariableNumber;i++) {
		pIP->variables[i].pData = NULL;
		pIP->variables[i].pVI = NULL;
	}
	pIP->variableCount = VariableNumber;

	return 0;
}

// load a variable/~array to an index
int APInterpreterLoadVar (TAPInterpreter IP, int msgEndian, int32_t index, int32_t varTypeID, int32_t numberOfElements)
{
	TAPrealInterpreter * pIP = (TAPrealInterpreter *) IP;
	if (msgEndian != pIP->pAP->sysEndian) {
		APendianConversation32Bit((uint32_t *)&index);
		APendianConversation32Bit((uint32_t *)&varTypeID);
	}

	if ((index < 0) || (index > pIP->variableCount)) return -1;

	// set pointer to the runtime variable
	TAPInterpreterVariable * pRTV = pIP->variables + index;
	THAL_Variable const * pV = HALfindVar(varTypeID);
	if (!pV) return -2;

	pRTV->pData = pV->pFkt_create((unsigned int)numberOfElements);
	//if (!pRTV->pData) return -3;

	pRTV->pVI = pV;
	return 0;
}

// load a single Instruction to an index
int APInterpreterLoadInstr (TAPInterpreter IP,int msgEndian, int32_t index, int32_t * pRawInstr)
{
	TAPrealInterpreter * pIP = (TAPrealInterpreter *) IP;
	if (msgEndian != pIP->pAP->sysEndian) {
		APendianConversation32Bit((uint32_t *)&index);
	}
	if ((index < 0) || (index > pIP->instructionCount)) return -1;
	TAPInterpreterFuncCall * pIFC = pIP->code + index;
	memset (pIFC, 0, sizeof(TAPInterpreterFuncCall));

	// get function
	int32_t fid = *pRawInstr;
	if (msgEndian != pIP->pAP->sysEndian) {
		APendianConversation32Bit((uint32_t *)&fid);
	}
	THALFunction const * pF = HALfindFunction(fid);
	if (!pF) return -2;
	pIFC->pHALFkt = pF->pfktHAL;

	// convert parameters
	pRawInstr++; // set to the first parameter
	int i;
	THALFunctionParam const * pP = pF->paramList.pL;
	TuAPInterpreterFunctionParameter * pIFP = pIFC->param;
	for (i = 0; i < pF->paramList.number; i++) {
		if (APconvertRawParamData (msgEndian,APInterpreterGetAPfromIP(IP),pRawInstr,pP,pIFP,pIP->variables)) return -3;
		pP++;
		pRawInstr++;
		pIFP++;
	}
	return 0;
}

// gets the varaible by it's index
TAPInterpreterVariable * APInterpreterGetVariableByIndex (TAPInterpreter IP, int index) {
	return &(((TAPrealInterpreter *) IP)->variables[index]);
}

// gets the AP from the IP
void * APInterpreterGetAPfromIP (TAPInterpreter IP) {
	return ((TAPrealInterpreter *) IP)->pAP;
}


typedef struct SAPrealMsgSystem {
	TAPMsg * 			pOldRXMsg;		// pointer to the oldest received messages
	TAPMsg * 			pNewRXMsg;		// pointer to the newest received messages
	TAPMMU				mmu;			// the mmu
	int					sysEndianness;	// the system endianness
	int					messagecounter;	// a counter for checkin if a new message has been received


} TAPrealMsgSystem;


int SMinitial (
		void *			pVoidSM,		// pointer to the statemachine
		uint32_t * 		pD,				// pointer to the data
		int				number			// the number of data elements
	);

int SMdata (
		void *			pVoidSM,		// pointer to the statemachine
		uint32_t * 		pD,				// pointer to the data
		int				number			// the number of data elements
	);

int SMmessageFinished (
		void *			pVoidSM		// pointer to the statemachine
	);



// create AP message system
TAPMsgSystem APMScreate (
		TAPMMU				mmu,			// the mmu
		int					sysEndianness	// the system endianness
	) {
	TAPrealMsgSystem * pMS = (TAPrealMsgSystem *) malloc (sizeof(TAPrealMsgSystem));
	if (!pMS) return NULL;
	pMS->mmu = mmu;
	pMS->sysEndianness = sysEndianness;
	pMS->pOldRXMsg = NULL;
	pMS->pNewRXMsg = NULL;
	pMS->messagecounter = 0;


	return pMS;
}

void APMSdelete (
	TAPMsgSystem ms
	) {
	TAPrealMsgSystem * pMS = (TAPrealMsgSystem *) ms;
	free (pMS);
}


// frees a message from the message system
void APMSdeleteMsg (
	TAPMsgSystem 	ms,
	TAPMsg *		pM
	) {
	TAPrealMsgSystem * pMS = (TAPrealMsgSystem *) ms;
	AP_MMU_free(pMS->mmu,pM->memory);
}

// get memory for a new message
TAPMsg * APMSgetNewMsg (
		TAPrealMsgSystem *	pMsgSys,
		int					dataElementsNumber,
		const TAPMsgDrv *	pDrv
		) {
	TAPMMUmemmory m = AP_MMU_get (pMsgSys->mmu,sizeof(TAPMsg)/sizeof(int) + dataElementsNumber*sizeof(uint32_t)/sizeof(int));
	if (!m) return NULL;

	int * pRD = (int *) AP_MMU_getData(m);
	TAPMsg * pM = (TAPMsg *) pRD;
	pM->memory = m;
	pM->extraData.pDrv = pDrv;
	pM->pH = (TAPMsgHeader *)((int *) pRD + sizeof(TAPMsg)/sizeof(int));
	pM->pData = (int *)pM->pH + sizeof(TAPMsgHeader)/sizeof(int);
	pM->pNext = NULL;
	return pM;
}

// insert a new message into the message queue
void APMSInsertMsg (
		TAPrealMsgSystem *	pMS,
		TAPMsg *			pM
	) {

	if (pMS->pNewRXMsg) {
		pMS->pNewRXMsg->pNext = pM;
	}
	pMS->pNewRXMsg = pM;
	if (!pMS->pOldRXMsg) {
		pMS->pOldRXMsg = pM;
	}
	pMS->messagecounter++;

}

// get oldest message
TAPMsg * APMSgetMsg (
		TAPMsgSystem 		ms,				// the message system
		TAPMessageID		msgID,			// if 0 all messages are allowed
		TAPNodeID			sender,			// if 0 all senders are allowed
		uint32_t			mNumber,		// if 0 all numbers are allowed
		int					ackMsgAllowed
	) {

	TAPrealMsgSystem * pMS = (TAPrealMsgSystem *) ms;
	// flags
	int senderOK;
	int msgIDok;
	int numberOK;
	// temp vars
	TAPMsg * pM;
	TAPMsg * pAntecessorM;
	uint32_t * pH;

	// result var
	TAPMsg * res = NULL;

checkMessages:

	// search msg list
	pM = pMS->pOldRXMsg;
	pAntecessorM =  NULL;

	if (!pM) goto waitForMessage;


	senderOK = 0;
	msgIDok = 0;
	numberOK = 0;

	pH = *(pM->pH);

	if (!sender) {
		senderOK = 1;
	} else {
		if (pH[eAPMsgHeaderPosition_sender] == sender) senderOK = 1;
	}
	if (!msgID) {
		// filter ack/nack msg
		if (ackMsgAllowed) {
			msgIDok = 1;
		} else {
			if (
					(pH[eAPMsgHeaderPosition_msgTypeID] != eAPMsgTypes_ACK)  &&
					(pH[eAPMsgHeaderPosition_msgTypeID] != eAPMsgTypes_NACK)
				) {
				msgIDok = 1;
			}
		}
	} else {
		if (pH[eAPMsgHeaderPosition_msgTypeID] == msgID) msgIDok = 1;
	}
	if (!mNumber) {
		numberOK = 1;
	} else {
		if (pH[eAPMsgHeaderPosition_msgNumber] == mNumber) numberOK = 1;
	}
	if ((senderOK) && (msgIDok) && (numberOK)) {
		res = pM;
		goto exit;
	}
	pAntecessorM = pM;
	pM = pM->pNext;
	if (pM) goto checkMessages;
waitForMessage:
	goto checkMessages;

exit:
	if (pAntecessorM) {
		pAntecessorM->pNext = pM->pNext;
	} else {
		pMS->pOldRXMsg = pM->pNext;
	}
	if (pM == pMS->pNewRXMsg) {
		pMS->pNewRXMsg = NULL;
	}

	// now there is one message less left
	pMS->messagecounter--;
	// now one thread is less waiting for a message
	return res;
error:
	return NULL;
}


// wait till a new message has been received
void APMSwaitForNewMessage (TAPrealMsgSystem * pMS)
{
	volatile int mc;

	mc = pMS->messagecounter;

	while (mc == pMS->messagecounter) {

	}
}

// returns 0 if a new message is available
inline int APMSisMessageAvailble (TAPrealMsgSystem * pMS) {
	return (!pMS->pOldRXMsg) ? 0 : -1;
}

// ========================================
// the receive state machine
// ========================================

// the receive state machine state function for receiving the msg header
int SMinitial (
		void *			pVoidSM,		// pointer to the statemachine
		uint32_t * 		pD,				// pointer to the data
		int				number			// the number of data elements
	) {
	TAPReceiveStateMachine * 			pSM = (TAPReceiveStateMachine *) pVoidSM;
	TAPrealMsgSystem *					pMS = (TAPrealMsgSystem *) pSM->pMS;
	int 								copyAmount = number;
	int									i;

	// 1. try to copy the data to the header
	if (pSM->elementsLeft < copyAmount) copyAmount = pSM->elementsLeft;
	// copy
	for (i = 0; i < copyAmount;i++) {
		*pSM->pD = *pD;
		pSM->pD++;
		pD++;
	}
	pSM->elementsLeft -= copyAmount;

	// check if we have to change the statemachine because we received the header
	if (pSM->elementsLeft) return 0;

	// yes! alloc msg buffer and (opt.) transfer data

	// 2. convert endian
	int msgEndian = pSM->header[eAPMsgHeaderPosition_endian];
	if (pMS->sysEndianness != msgEndian) {
		for (i = 0; i < eAPMsgHeaderPosition_headerElementNumber;i++) {
			APendianConversation32Bit(&pSM->header[i]);
		}
	}
	// 3. now alloc message
	// 3.1 get length
	int msgElementNumber = (int) pSM->header[eAPMsgHeaderPosition_length];
	// 3.2. get memory
	pSM->pMsg = APMSgetNewMsg (pMS,msgElementNumber,pSM->pDrv);
	if (!pSM->pMsg) return -100;

	// 3.3 check getMemory result
	if (!pSM->pMsg) return -1;
	// copy message header
	pSM->pD = (uint32_t *) pSM->pMsg->pH;
	for (i = 1; i < eAPMsgHeaderPosition_headerElementNumber;i++) {
		*pSM->pD = pSM->header[i];
		pSM->pD++;
	}
	pSM->elementsLeft = pSM->header[eAPMsgHeaderPosition_length];
	// set up the data
	// 1. check if there is an data element
	if (!pSM->elementsLeft) {
		// no! now finish the message
		return SMmessageFinished(pVoidSM);
	}
	// 2. yes
	// 2.1 setup the sm for the data receiving
	pSM->state = SMdata;
	// 2.2 now check if we have to copy some data
	number -= copyAmount;
	if (number) {
		// set the data pointer
		pD += copyAmount;
		// and copy the data
		return SMdata (pVoidSM,pD,number);
	}
	return 0;
}


// the receive state machine state function for receiving the data
int SMdata (
		void *			pVoidSM,		// pointer to the statemachine
		uint32_t * 		pD,				// pointer to the data
		int				number			// the number of data elements
	) {
	TAPReceiveStateMachine * 			pSM = (TAPReceiveStateMachine *) pVoidSM;
	int 								copyAmount = number;
	int									i;
	// 1. transfer the data
	// do some clipping
	if (pSM->elementsLeft < copyAmount) copyAmount = pSM->elementsLeft;
	// copy
	for (i = 0; i < copyAmount;i++) {
		*pSM->pD = *pD;
		pSM->pD++;
		pD++;
	}
	// set statemachine work data
	pSM->elementsLeft -= copyAmount;
	// check if we have to change the statemachine
	if (pSM->elementsLeft) return 0;
	int res = SMmessageFinished (pVoidSM);
	if (res) return res;

	// check if there some bytes left to copy
	number -= copyAmount;
	if (number) {
		// set the data pointer
		pD += copyAmount;
		// and copy the data
		return pSM->state (pVoidSM,pD,number);
	}
	return 0;

}

// this function is called when all data have been received
int SMmessageFinished (
		void *			pVoidSM		// pointer to the statemachine
	) {
	TAPReceiveStateMachine * 			pSM = (TAPReceiveStateMachine *) pVoidSM;
	TAPMsg *							pM;
	// 1. reset SM
	// set the helper
	pSM->elementsLeft = eAPMsgHeaderPosition_headerElementNumber;
	pSM->pD = pSM->header;

	// data
	pM = pSM->pMsg; // save msg info for inserting
	pSM->pMsg = NULL;

	// right state function
	pSM->state = SMinitial;		// the state

	// 2. insert message at the message system
	APMSInsertMsg ((TAPrealMsgSystem *)pSM->pMS,pM);
	return 0;
}


// inits the state machine
void APInitReceiveStateMachine (
		TAPReceiveStateMachine * 			pSM,	// pointer to the state machine
		TAPMsgSystem						pMS,	// pointer to the message system
		const TAPMsgDrv  *					pDrv	// the driver associated with the statemachine
	) {
	pSM->state = SMinitial;
	pSM->pMS = pMS;
	pSM->pDrv = pDrv;
	// set the helper
	pSM->elementsLeft = eAPMsgHeaderPosition_headerElementNumber;
	pSM->pD = pSM->header;

	// data
	pSM->pMsg = NULL;
}

int APHandleMsg (
		TAP *		pAP,
		TAPMsg * 	pM
	) {

	TAPMessageID								msgID;
	const THALMsgProcessMessageAssociation *	pMsgIDandFunctAsso;
	int											i;

	// get message id
	msgID = (*(pM->pH))[eAPMsgHeaderPosition_msgTypeID];
	// search handler
	pMsgIDandFunctAsso = gHALMsgProcessRXHandlers.pL;
	for (i = 0; i < gHALMsgProcessRXHandlers.number;i++) {
		if (((TAPMessageID)pMsgIDandFunctAsso->msgID) == msgID) {
			return pMsgIDandFunctAsso->pfktHandle(pAP,pM);
		}
		pMsgIDandFunctAsso++;
	}
	return -1;
}

void APMessageProcessingThread (TAP * pAP) {
	TAPrealMsgSystem *		pMS = (TAPrealMsgSystem *) pAP->MS;
	TAPMsg *				pM;
	TAPNodeID 				recv;
	while (1) {
		// get the message
		pM = APMSgetMsg (pMS,0,0,0,0);
		if (!pM) goto error;
		// search the message handler
		recv = (*(pM->pH))[eAPMsgHeaderPosition_receiver];
		if ((recv == dAPNodeID_ALL) || (recv == pAP->nodeID)) {
			if(APHandleMsg (pAP,pM)) goto exit;
		}
		// free memory
		APMSdeleteMsg (pAP->MS,pM);
	}
exit:
	// free memory
	APMSdeleteMsg (pAP->MS,pM);
error:
}

// ===============================
// AP uuid = 11
// ===============================

// inits the AP
int APinit (
			TAP *				pAP,
			TAPNodeID			nodeID,
			const TAPMsgDrv *	pDrvList,
			const int			driverNumber,
			size_t				messagePoolSize,
			int					sysEndian
		)
{
	pAP->nodeID = nodeID;
	pAP->pNodeList = NULL;
	pAP->pDrvList = pDrvList;
	pAP->sysEndian = sysEndian;
	pAP->driverNumber = driverNumber;
	pAP->msgSysMMU = AP_MMU_create(messagePoolSize);
	pAP->IP = APInterpreterCreate(pAP);
	pAP->MS = APMScreate (pAP->msgSysMMU,sysEndian);
	pAP->msgNumber = 0;
	pAP->APstate = eAPstate_idle;

	if(
		(!pAP->msgSysMMU)||
		(!pAP->IP)||
		(!pAP->MS)
		) return -1;

	msp430_initHW(10);

	// init drv
	TAPMsgDrv * pDrv = (TAPMsgDrv *)pDrvList;
	int i;
	for (i = 0; i < driverNumber; i++) {
		pDrv->pfkt_open(pAP, pDrv);
		pDrv++;
	}

	// login the ap to the message system
	return TX_login(pAP);
}

// deletes the AP
void APdelete (TAP * pAP)
{
	APMSdelete (pAP->MS);
	APInterpreterDelete(pAP->IP);
	AP_MMU_delete(pAP->msgSysMMU);
}

// find a node at the list
TAPNode * APfindNode(TAP * pAP, TAPNodeID nodeID) {
	TAPNode * pN = pAP->pNodeList;
	while (pN) {
		if (pN->nodeID == nodeID) return pN;
		pN = pN->pNext;
	};
	return NULL;
}

// adds a new node to the node list
int APaddNode(TAP * pAP, TAPNodeID newNodeID, const TAPMsgDrv * pDrv) {
	if (APfindNode(pAP,newNodeID)) return 1;
	TAPNode * pN = (TAPNode *) malloc(sizeof(TAPNode));
	if (!pN) return -1;
	pN->nodeID = newNodeID;
	pN->pDrv = pDrv;
	pN->pNext = pAP->pNodeList;
	pAP->pNodeList = pN;
	return 0;
}

// removes a node from the node list
void APremoveNode(TAP * pAP, TAPNodeID nodeID){
	TAPNode * pAntN = pAP->pNodeList; // antecessor node
	TAPNode * pActN = pAP->pNodeList; // actual node

	while (pActN) {
		// compare node id's
		if (pActN->nodeID == nodeID) {
			// unchain

			// check if we at the first position at the list
			if (pAP->pNodeList == pAntN) {
				// reset the pointer
				pAP->pNodeList = pActN->pNext;
			} else {
				// set the antecessor
				pAntN->pNext = pActN->pNext;
			}
			// free node
			free(pActN);
			// and abort
			return;
		}
		// the actual element becomes the precessor element
		pAntN = pActN;
		pActN = pActN->pNext;
	}
}

// get a new message number
unsigned int APgetNewMessageNumber (TAP *pAP) {
	pAP->msgNumber++;
	return pAP->msgNumber;
}

// find the driver associated with der nodeID
const TAPMsgDrv * APfindDrvBySenderID (TAP * pAP, TAPNodeID node) {
	TAPNode * pN = pAP->pNodeList;
	while (pN) {
		if (pN->nodeID == node) {
			return pN->pDrv;
		}
		pN = pN->pNext;
	}
	return NULL;
}

// runs the AP
int APrun(TAP *pAP) {
	pAP->APstate = eAPstate_run;
	return 0;
}

// ================================
// MMU functions
// ================================

typedef struct SAPrealMMUMemory {
	uint32_t *					pData;			// the data
	int							count;			// amount of data elements
	struct SAPrealMMUMemory *	pNext;			// next element
	struct SAPrealMMUMemory *	pPrev;			// previous element
} TAPrealMMUMemory;

//the mmu type
typedef struct SAPrealMMU {
	uint32_t *				memory;				// the memory block
	TAPrealMMUMemory *		pStart;				// first element
	TAPrealMMUMemory *		pEnd;				// second element
	TAPrealMMUMemory *		pUnusedList;		// list with the unused elements
	uint32_t  * 			pUnusedData;		// pointer to the unused memory
	int						elementsAvailable; 	// amount of elements witch are available without using the garbage collector
	int						totalAvailable;		// total amount of free bytes

} TAPrealMMU;

// ========================================
// memory entry functions
// ========================================

// a little macro for unchaining an element
#define DMemoryEntryUnchain(pM) \
	if (pM->pNext) pM->pNext->pPrev = pM->pPrev; \
	if (pM->pPrev) pM->pPrev->pNext = pM->pNext


//creates an memory entry
TAPrealMMUMemory * MemoryEntry_create () {
	TAPrealMMUMemory * pM = NULL;

	pM = (TAPrealMMUMemory *) malloc(sizeof(TAPrealMMUMemory));
	if (!pM) return NULL;
	pM->pData = NULL;
	pM->count = 0;
	pM->pNext = NULL;
	pM->pPrev = NULL;

	return pM;
}

//deletes an memory Entry
void MemoryEntry_delete (
		TAPrealMMUMemory * pM	// the memory to delete
		)
{
	// put the entry out of the chain
	DMemoryEntryUnchain(pM);
	// now we delete it
	free(pM);
}

// ========================================
// mmu helper
// ========================================

//alloc if needed a new memory entry
TAPrealMMUMemory * MMU_helper_createMemoryEntry (
		TAPrealMMU *	pMMU 		// MMU structure to init
		)
{
	// check if we have to alloc a new memory entry
	if (!pMMU->pUnusedList) return MemoryEntry_create();
	// no there is some left at the list
	TAPrealMMUMemory * pM;
	// take the first one
	pM = pMMU->pUnusedList;
	// reset the list
	pMMU->pUnusedList = pM->pNext;
	// now unchain the element (for sure)
	DMemoryEntryUnchain(pM);
	// set the element pointers
	pM->pNext = NULL;
	pM->pPrev = NULL;
	return pM;
}

//the garbage collector
void MMU_helper_garbageCollector (
		TAPrealMMU *	pMMU 		// MMU structure to init
		)
{
	TAPrealMMUMemory * pM = pMMU->pStart;
	uint32_t * pD = pMMU->memory;
	while (pM) {
		// check if we have to move the data
		if (pD != pM->pData) {
			// move the data
			memmove(pD,pM->pData,pM->count*sizeof(uint32_t));
		}
		// reset the destination pointer
		pD += pM->count;
		pM = pM->pNext;
	}
	// compressing memory finished
	// set the mmu vars new
	pMMU->elementsAvailable = pMMU->totalAvailable;
	pMMU->pUnusedData = pD;
}

// create a mmu
TAPMMU AP_MMU_create (int elementsNumber) {
	TAPrealMMU * pMMU;

	pMMU = (TAPrealMMU *) malloc (sizeof(TAPrealMMU));
	if (!pMMU) return NULL;


	pMMU->memory = (uint32_t *) malloc (elementsNumber*sizeof(uint32_t));
	pMMU->pUnusedData = pMMU->memory;

	// setup lists
	pMMU->pStart = NULL;
	pMMU->pEnd = NULL;
	pMMU->pUnusedList = NULL;

	pMMU->elementsAvailable =elementsNumber;

	pMMU->totalAvailable = elementsNumber;
	return pMMU;
}

// destroying the mmu
void AP_MMU_delete (TAPMMU mmu) {
	TAPrealMMU * pMMU = (TAPrealMMU *) mmu;

	TAPrealMMUMemory * pM;
	TAPrealMMUMemory * pMnext;

// 1. delete al mmu entry's
	// 1.1 unused entry
	pM = pMMU->pUnusedList;
	while (pM) {
		pMnext = pM->pNext;
		MemoryEntry_delete(pM);
		pM = pMnext;
	}
	pMMU->pUnusedList = NULL;
	// 1.2 used blocks
	pM = pMMU->pStart;
	while (pM) {
		pMnext = pM->pNext;
		MemoryEntry_delete(pM);
		pM = pMnext;
	}
	pMMU->pStart = NULL;
	pMMU->pEnd = NULL;
// 2. delete mmu memory
	free (pMMU->memory);
}

// getting memmory from the mmu
TAPMMUmemmory AP_MMU_get (TAPMMU mmu, int elements) {
	TAPrealMMU * pMMU = (TAPrealMMU *) mmu;

	// check if there is enough space
	if (pMMU->totalAvailable < elements) goto error;
	// check if we have to use the garbage collector
	if (pMMU->elementsAvailable < elements) {
		// start garbage collector
		MMU_helper_garbageCollector(pMMU);
	}
	// we have enough memory so let's allocate some

	// get a new entry
	TAPrealMMUMemory * pM;
	pM = MMU_helper_createMemoryEntry(pMMU);
	if (!pM) return NULL;
	// get some memory
	pM->pData = pMMU->pUnusedData;
	pM->count = elements;
	// refresh data
	pMMU->pUnusedData += elements;
	pMMU->totalAvailable -= elements;
	pMMU->elementsAvailable -= elements;
	// insert memory element at the end of the list and update last element
	pM->pPrev = pMMU->pEnd;
	if (pMMU->pEnd) pMMU->pEnd->pNext = pM;
	if (!pMMU->pStart) pMMU->pStart = pM;
	pMMU->pEnd = pM;
	return pM;
error:
	return NULL;
}

// free memmory from the mmu
void AP_MMU_free (TAPMMU mmu, TAPMMUmemmory memory) {
	TAPrealMMU * pMMU = (TAPrealMMU *) mmu;
	TAPrealMMUMemory * pM = (TAPrealMMUMemory *) memory;

	if (!pM) return;
	// set mmu settings
	if (pMMU->pStart == pM) pMMU->pStart = pM->pNext;
	if (pMMU->pEnd == pM) pMMU->pEnd = pM->pPrev;
	// unchain element
	DMemoryEntryUnchain (pM);
	// and put it to the chain of unused
	pM->pNext = pMMU->pUnusedList;
	pM->pPrev = NULL;
	if (pMMU->pUnusedList) {
		pMMU->pUnusedList->pPrev = pM;
	}
	pMMU->pUnusedList = pM;
	// now set the mmu data new
	pMMU->totalAvailable += pM->count;
}

// getting access to the MMU data
void * AP_MMU_getData (TAPMMUmemmory memory) {
	TAPrealMMUMemory * pM = (TAPrealMMUMemory *) memory;
	return pM->pData;
}

// the real interpreter
typedef struct SAPrealInterpreter {
	TAP *						pAP;					// pointer to the audio processor
	int							state;					// state of the IP
	int							nextState;				// the next state of the IP
	TAPInterpreterCPU			cpu;					// the IP core
	TAPInterpreterFuncCall *	code;					// the code
	int32_t						instructionCount;		// number of instructions at the code
	TAPInterpreterVariable *	variables;				// the variables
	int32_t						variableCount;			// number of the variables
} TAPrealInterpreter;

// create a new interpreter
TAPInterpreter APInterpreterCreate (void * pAP) {
	TAPrealInterpreter * pIP = NULL;
	pIP = (TAPrealInterpreter *) malloc (sizeof(TAPrealInterpreter));
	if (!pIP) return NULL;

	pIP->pAP = pAP;
	pIP->state = eAPInterpreterState_idle;
	pIP->nextState = eAPInterpreterState_idle;
	pIP->cpu.IP = pIP;
	pIP->cpu.CF = 0;
	pIP->cpu.EF = 0;
	pIP->cpu.pCodeStart = NULL;
	pIP->cpu.pCodeEnd = NULL;
	pIP->cpu.pIP = NULL;

	pIP->code = NULL;
	pIP->instructionCount = 0;

	pIP->variables = NULL;
	pIP->variableCount = 0;

	return pIP;
}

// cleans the interpreter
void APInterpreterClean (TAPInterpreter IP) {
	TAPrealInterpreter * pIP = (TAPrealInterpreter *) IP;

	// clean code
	if (pIP->code) {
		free (pIP->code);
		pIP->code = NULL;
	}
	pIP->instructionCount = 0;

	// clean variables
	TAPInterpreterVariable * pV = pIP->variables;
	int i;
	for (i = 0; i < pIP->variableCount; i++) {
		if (pV->pVI) pV->pVI->pFkt_delete(pV->pData);
		pV++;
	}
	if (pIP->variables) {
		free (pIP->variables);
		pIP->variables = NULL;
	}
	pIP->variableCount = 0;

}

// deletes the interpreter
void APInterpreterDelete (TAPInterpreter IP) {
	TAPrealInterpreter * pIP = (TAPrealInterpreter *) IP;
	APInterpreterClean(IP);
	free (pIP);
}

extern void drv_3_feedRecvStateM (void);

int APInterpreterStateRun(TAPInterpreter IP) {
	TAPrealInterpreter * pIP = (TAPrealInterpreter *) IP;
	TAPInterpreterFuncCall * pFC;
	int cc; // cycle counter

	// setup cpu
	pIP->cpu.CF = 0;
	pIP->cpu.EF = 0;
	pIP->cpu.pIP = pIP->code;
	pIP->cpu.pCodeStart = pIP->code;
	pIP->cpu.pCodeEnd = pIP->code + pIP->instructionCount;

	// run code
	cc = 10;
	while (eAPInterpreterState_run == pIP->state) {
		pFC = pIP->cpu.pIP;
		// check if we reached the end of the code
		if (pFC > pIP->cpu.pCodeEnd) {
			return 0;
		}
		// execute command
		pFC->pHALFkt (&(pIP->cpu), pFC->param);
		// check error flags
		if (pIP->cpu.EF) {
			return -1;
		}
		// process msg receiving
		drv_3_feedRecvStateM ();

		// check message system after x cycles
		if (!cc) {
			APMessageProcess(pIP->pAP);
			cc = 100;
		} else {
			cc--;
		}

	}
	return 1;
}


// process the actual state
int APInterpreterProcessState(TAPInterpreter IP){
	TAPrealInterpreter * pIP = (TAPrealInterpreter *) IP;
	pIP->state = pIP->nextState;
	int rc = 0;

	switch (pIP->state) {
		case eAPInterpreterState_idle:
			break;
		case eAPInterpreterState_loadProgramm:
			break;
		case eAPInterpreterState_run:
			rc = APInterpreterStateRun(IP);
			if (rc >= 0) pIP->state = eAPInterpreterState_idle;
			break;
		case eAPInterpreterState_oneStep:
			break;
		case eAPInterpreterState_halt:
			break;
		default:
			return -10;
	}
	return rc;
}


// set interpreter state
int APInterpreterSetState (TAPInterpreter IP, int msgEndian, int32_t state) {
	TAPrealInterpreter * pIP = (TAPrealInterpreter *) IP;
	if (msgEndian != pIP->pAP->sysEndian) {
		APendianConversation32Bit((uint32_t *)&state);
	}
	pIP->nextState = (int) state;
	return 0;
}

// setup the interpreter for a new program
int APInterpreterInitNewProgramm (TAPInterpreter IP, int msgEndian, int32_t instructionsNumber, int32_t VariableNumber) {
	TAPrealInterpreter * pIP = (TAPrealInterpreter *) IP;
	int i;

	APInterpreterClean (IP);

	if (msgEndian != pIP->pAP->sysEndian) {
		APendianConversation32Bit((uint32_t *)&instructionsNumber);
		APendianConversation32Bit((uint32_t *)&VariableNumber);
	}

	pIP->code = (TAPInterpreterFuncCall *) malloc(sizeof(TAPInterpreterFuncCall)*instructionsNumber);
	pIP->instructionCount = instructionsNumber;

	pIP->variables = (TAPInterpreterVariable *) malloc(sizeof(TAPInterpreterVariable) * (VariableNumber));
	for (i = 0; i < VariableNumber;i++) {
		pIP->variables[i].pData = NULL;
		pIP->variables[i].pVI = NULL;
	}
	pIP->variableCount = VariableNumber;

	return 0;
}

// load a variable/~array to an index
int APInterpreterLoadVar (TAPInterpreter IP, int msgEndian, int32_t index, int32_t varTypeID, int32_t numberOfElements)
{
	TAPrealInterpreter * pIP = (TAPrealInterpreter *) IP;
	if (msgEndian != pIP->pAP->sysEndian) {
		APendianConversation32Bit((uint32_t *)&index);
		APendianConversation32Bit((uint32_t *)&varTypeID);
	}

	if ((index < 0) || (index > pIP->variableCount)) return -1;

	// set pointer to the runtime variable
	TAPInterpreterVariable * pRTV = pIP->variables + index;
	THAL_Variable const * pV = HALfindVar(varTypeID);
	if (!pV) return -2;

	pRTV->pData = pV->pFkt_create((unsigned int)numberOfElements);
	//if (!pRTV->pData) return -3;

	pRTV->pVI = pV;
	return 0;
}

// load a single Instruction to an index
int APInterpreterLoadInstr (TAPInterpreter IP,int msgEndian, int32_t index, int32_t * pRawInstr)
{
	TAPrealInterpreter * pIP = (TAPrealInterpreter *) IP;
	if (msgEndian != pIP->pAP->sysEndian) {
		APendianConversation32Bit((uint32_t *)&index);
	}
	if ((index < 0) || (index > pIP->instructionCount)) return -1;
	TAPInterpreterFuncCall * pIFC = pIP->code + index;
	memset (pIFC, 0, sizeof(TAPInterpreterFuncCall));

	// get function
	int32_t fid = *pRawInstr;
	if (msgEndian != pIP->pAP->sysEndian) {
		APendianConversation32Bit((uint32_t *)&fid);
	}
	THALFunction const * pF = HALfindFunction(fid);
	if (!pF) return -2;
	pIFC->pHALFkt = pF->pfktHAL;

	// convert parameters
	pRawInstr++; // set to the first parameter
	int i;
	THALFunctionParam const * pP = pF->paramList.pL;
	TuAPInterpreterFunctionParameter * pIFP = pIFC->param;
	for (i = 0; i < pF->paramList.number; i++) {
		if (APconvertRawParamData (msgEndian,APInterpreterGetAPfromIP(IP),pRawInstr,pP,pIFP,pIP->variables)) return -3;
		pP++;
		pRawInstr++;
		pIFP++;
	}
	return 0;
}

// gets the varaible by it's index
TAPInterpreterVariable * APInterpreterGetVariableByIndex (TAPInterpreter IP, int index) {
	return &(((TAPrealInterpreter *) IP)->variables[index]);
}

// gets the AP from the IP
void * APInterpreterGetAPfromIP (TAPInterpreter IP) {
	return ((TAPrealInterpreter *) IP)->pAP;
}


typedef struct SAPrealMsgSystem {
	TAPMsg * 			pOldRXMsg;		// pointer to the oldest received messages
	TAPMsg * 			pNewRXMsg;		// pointer to the newest received messages
	TAPMMU				mmu;			// the mmu
	int					sysEndianness;	// the system endianness
	int					messagecounter;	// a counter for checkin if a new message has been received


} TAPrealMsgSystem;


int SMinitial (
		void *			pVoidSM,		// pointer to the statemachine
		uint32_t * 		pD,				// pointer to the data
		int				number			// the number of data elements
	);

int SMdata (
		void *			pVoidSM,		// pointer to the statemachine
		uint32_t * 		pD,				// pointer to the data
		int				number			// the number of data elements
	);

int SMmessageFinished (
		void *			pVoidSM		// pointer to the statemachine
	);



// create AP message system
TAPMsgSystem APMScreate (
		TAPMMU				mmu,			// the mmu
		int					sysEndianness	// the system endianness
	) {
	TAPrealMsgSystem * pMS = (TAPrealMsgSystem *) malloc (sizeof(TAPrealMsgSystem));
	if (!pMS) return NULL;
	pMS->mmu = mmu;
	pMS->sysEndianness = sysEndianness;
	pMS->pOldRXMsg = NULL;
	pMS->pNewRXMsg = NULL;
	pMS->messagecounter = 0;


	return pMS;
}

void APMSdelete (
	TAPMsgSystem ms
	) {
	TAPrealMsgSystem * pMS = (TAPrealMsgSystem *) ms;
	free (pMS);
}


// frees a message from the message system
void APMSdeleteMsg (
	TAPMsgSystem 	ms,
	TAPMsg *		pM
	) {
	TAPrealMsgSystem * pMS = (TAPrealMsgSystem *) ms;
	AP_MMU_free(pMS->mmu,pM->memory);
}

// get memory for a new message
TAPMsg * APMSgetNewMsg (
		TAPrealMsgSystem *	pMsgSys,
		int					dataElementsNumber,
		const TAPMsgDrv *	pDrv
		) {
	// we go for sure that we get enough memory
	// if mod(sizeof(TAPMsg)/sizeof(uint32_t)) != 0 we need one uint32_t more -> +1
	TAPMMUmemmory m = AP_MMU_get (pMsgSys->mmu,sizeof(TAPMsg)/sizeof(uint32_t) + 1+ eAPMsgHeaderPosition_headerElementNumber + dataElementsNumber);
	if (!m) return NULL;

	// set the pointers
	uint32_t * pRD = (uint32_t *) AP_MMU_getData(m);
	TAPMsg * pM = (TAPMsg *) pRD;
	pM->memory = m;
	pM->extraData.pDrv = pDrv;
	pM->pH = (TAPMsgHeader *)((uint32_t *) pRD + sizeof(TAPMsg)/sizeof(uint32_t)+1);
	pM->pData = (uint32_t *)pM->pH + sizeof(TAPMsgHeader)/sizeof(uint32_t);
	pM->pNext = NULL;
	return pM;
}

// insert a new message into the message queue
void APMSInsertMsg (
		TAPrealMsgSystem *	pMS,
		TAPMsg *			pM
	) {

	if (pMS->pNewRXMsg) {
		pMS->pNewRXMsg->pNext = pM;
	}
	pMS->pNewRXMsg = pM;
	if (!pMS->pOldRXMsg) {
		pMS->pOldRXMsg = pM;
	}
	pMS->messagecounter++;

}

// unchains a received message
void APMSunchainMessage (
		TAPrealMsgSystem *	pMS,
		TAPMsg *			pM,
		TAPMsg *			pAntecessorM
) {
	if (pAntecessorM) {
		pAntecessorM->pNext = pM->pNext;
	} else {
		pMS->pOldRXMsg = pM->pNext;
	}
	if (pM == pMS->pNewRXMsg) {
		pMS->pNewRXMsg = NULL;
	}

	// now there is one message less left
	pMS->messagecounter--;
}


// get oldest message
TAPMsg * APMSgetMsg (
		TAPMsgSystem 		ms,				// the message system
		TAPMessageID		msgID,			// if 0 all messages are allowed
		TAPNodeID			sender,			// if 0 all senders are allowed
		uint32_t			mNumber,		// if 0 all numbers are allowed
		int					ackMsgAllowed
	) {

	TAPrealMsgSystem * pMS = (TAPrealMsgSystem *) ms;
	// flags
	int senderOK;
	int msgIDok;
	int numberOK;
	// temp vars
	TAPMsg * pM;
	TAPMsg * pAntecessorM;
	uint32_t * pH;

	// result var
	TAPMsg * res = NULL;

checkMessages:
	// search msg list
	pM = pMS->pOldRXMsg;
	pAntecessorM =  NULL;

	if (!pM) goto waitForMessage;


	senderOK = 0;
	msgIDok = 0;
	numberOK = 0;

	pH = *(pM->pH);

	if (!sender) {
		senderOK = 1;
	} else {
		if (pH[eAPMsgHeaderPosition_sender] == sender) senderOK = 1;
	}
	if (!msgID) {
		// filter ack/nack msg
		if (ackMsgAllowed) {
			msgIDok = 1;
		} else {
			if (
					(pH[eAPMsgHeaderPosition_msgTypeID] != eAPMsgTypes_ACK)  &&
					(pH[eAPMsgHeaderPosition_msgTypeID] != eAPMsgTypes_NACK)
				) {
				msgIDok = 1;
			}
		}
	} else {
		if (pH[eAPMsgHeaderPosition_msgTypeID] == msgID) msgIDok = 1;
	}
	if (!mNumber) {
		numberOK = 1;
	} else {
		if (pH[eAPMsgHeaderPosition_msgNumber] == mNumber) numberOK = 1;
	}
	if ((senderOK) && (msgIDok) && (numberOK)) {
		res = pM;
		goto exit;
	}
	pAntecessorM = pM;
	pM = pM->pNext;
	if (pM) goto checkMessages;
waitForMessage:
	drv_3_feedRecvStateM();
	goto checkMessages;
exit:
	// unchain message
	APMSunchainMessage(pMS,pM,pAntecessorM);
	// now one thread is less waiting for a message
	return res;
error:
	return NULL;
}

// wait till a new message has been received
void APMSwaitForNewMessage (TAPrealMsgSystem * pMS)
{
	volatile int mc;

	mc = pMS->messagecounter;

	while (mc == pMS->messagecounter) {
		drv_3_feedRecvStateM();
	}
}

// returns 0 if a new message is available
inline int APMSisMessageAvailble (TAPrealMsgSystem * pMS) {
	return (!pMS->pOldRXMsg) ? 0 : -1;
}

// ========================================
// the receive state machine
// ========================================

// the receive state machine state function for receiving the msg header
int SMinitial (
		void *			pVoidSM,		// pointer to the statemachine
		uint32_t * 		pD,				// pointer to the data
		int				number			// the number of data elements
	) {
	TAPReceiveStateMachine * 			pSM = (TAPReceiveStateMachine *) pVoidSM;
	TAPrealMsgSystem *					pMS = (TAPrealMsgSystem *) pSM->pMS;
	int 								copyAmount = number;
	int									i;

	// 1. try to copy the data to the header
	if (pSM->elementsLeft < copyAmount) copyAmount = pSM->elementsLeft;
	// copy
	for (i = 0; i < copyAmount;i++) {
		*pSM->pD = *pD;
		pSM->pD++;
		pD++;
	}
	pSM->elementsLeft -= copyAmount;

	// check if we have to change the statemachine because we received the header
	if (pSM->elementsLeft) return 0;

	// yes! alloc msg buffer and (opt.) transfer data

	// 2. convert endian
	int msgEndian = pSM->header[eAPMsgHeaderPosition_endian];
	if (pMS->sysEndianness != msgEndian) {
		for (i = 0; i < eAPMsgHeaderPosition_headerElementNumber;i++) {
			APendianConversation32Bit(&pSM->header[i]);
		}
	}
	// 3. now alloc message
	// 3.1 get length
	int msgElementNumber = (int) pSM->header[eAPMsgHeaderPosition_length];
	// 3.2. get memory
	pSM->pMsg = APMSgetNewMsg (pMS,msgElementNumber,pSM->pDrv);
	if (!pSM->pMsg) return -100;

	// 3.3 check getMemory result
	if (!pSM->pMsg) return -1;
	// copy message header
	pSM->pD = (uint32_t *) pSM->pMsg->pH;
	for (i = 0; i < eAPMsgHeaderPosition_headerElementNumber;i++) {
		*pSM->pD = pSM->header[i];
		pSM->pD++;
	}
	pSM->elementsLeft = pSM->header[eAPMsgHeaderPosition_length];
	// set up the data
	// 1. check if there is an data element
	if (!pSM->elementsLeft) {
		// no! now finish the message
		return SMmessageFinished(pVoidSM);
	}
	// 2. yes
	// 2.1 setup the sm for the data receiving
	pSM->state = SMdata;
	// 2.2 now check if we have to copy some data
	number -= copyAmount;
	if (number) {
		// set the data pointer
		pD += copyAmount;
		// and copy the data
		return SMdata (pVoidSM,pD,number);
	}
	return 0;
}


// the receive state machine state function for receiving the data
int SMdata (
		void *			pVoidSM,		// pointer to the statemachine
		uint32_t * 		pD,				// pointer to the data
		int				number			// the number of data elements
	) {
	TAPReceiveStateMachine * 			pSM = (TAPReceiveStateMachine *) pVoidSM;
	int 								copyAmount = number;
	int									i;
	// 1. transfer the data
	// do some clipping
	if (pSM->elementsLeft < copyAmount) copyAmount = pSM->elementsLeft;
	// copy
	for (i = 0; i < copyAmount;i++) {
		*pSM->pD = *pD;
		pSM->pD++;
		pD++;
	}
	// set statemachine work data
	pSM->elementsLeft -= copyAmount;
	// check if we have to change the statemachine
	if (pSM->elementsLeft) return 0;
	int res = SMmessageFinished (pVoidSM);
	if (res) return res;

	// check if there some bytes left to copy
	number -= copyAmount;
	if (number) {
		// set the data pointer
		pD += copyAmount;
		// and copy the data
		return pSM->state (pVoidSM,pD,number);
	}
	return 0;

}

// this function is called when all data have been received
int SMmessageFinished (
		void *			pVoidSM		// pointer to the statemachine
	) {
	TAPReceiveStateMachine * 			pSM = (TAPReceiveStateMachine *) pVoidSM;
	TAPMsg *							pM;
	// 1. reset SM
	// set the helper
	pSM->elementsLeft = eAPMsgHeaderPosition_headerElementNumber;
	pSM->pD = pSM->header;

	// data
	pM = pSM->pMsg; // save msg info for inserting
	pSM->pMsg = NULL;

	// right state function
	pSM->state = SMinitial;		// the state

	// 2. insert message at the message system
	APMSInsertMsg ((TAPrealMsgSystem *)pSM->pMS,pM);
	return 0;
}


// inits the state machine
void APInitReceiveStateMachine (
		TAPReceiveStateMachine * 			pSM,	// pointer to the state machine
		TAPMsgSystem						pMS,	// pointer to the message system
		const TAPMsgDrv  *					pDrv	// the driver associated with the statemachine
	) {
	pSM->state = SMinitial;
	pSM->pMS = pMS;
	pSM->pDrv = pDrv;
	// set the helper
	pSM->elementsLeft = eAPMsgHeaderPosition_headerElementNumber;
	pSM->pD = pSM->header;

	// data
	pSM->pMsg = NULL;
}

int APHandleMsg (
		TAP *		pAP,
		TAPMsg * 	pM
	) {

	TAPMessageID								msgID;
	const THALMsgProcessMessageAssociation *	pMsgIDandFunctAsso;
	int											i;

	// get message id
	msgID = (*(pM->pH))[eAPMsgHeaderPosition_msgTypeID];
	// search handler
	pMsgIDandFunctAsso = gHALMsgProcessRXHandlers.pL;
	for (i = 0; i < gHALMsgProcessRXHandlers.number;i++) {
		if (((TAPMessageID)pMsgIDandFunctAsso->msgID) == msgID) {
			return pMsgIDandFunctAsso->pfktHandle(pAP,pM);
		}
		pMsgIDandFunctAsso++;
	}
	return -1;
}

void APMessageProcessingThread (TAP * pAP) {
	TAPrealMsgSystem *		pMS = (TAPrealMsgSystem *) pAP->MS;
	TAPMsg *				pM;
	TAPNodeID 				recv;
	while (1) {
		// get the message
		pM = APMSgetMsg (pMS,0,0,0,0);
		if (!pM) goto error;
		// search the message handler
		recv = (*(pM->pH))[eAPMsgHeaderPosition_receiver];
		if ((recv == dAPNodeID_ALL) || (recv == pAP->nodeID)) {
			if(APHandleMsg (pAP,pM)) goto exit;
		}
		// free memory
		APMSdeleteMsg (pAP->MS,pM);
	}
exit:
	// free memory
	APMSdeleteMsg (pAP->MS,pM);
error:
	return;
}

// if a message is in the queue available it will be processed
void APMessageProcess (TAP * pAP) {
	TAPrealMsgSystem *		pMS = (TAPrealMsgSystem *) pAP->MS;
	TAPMsg *				pM;
	TAPNodeID				recvID;

	// search msg list
	pM = pMS->pOldRXMsg;

	// if there is no message we will return
	if (!pM) return;

	// unchain first message
	APMSunchainMessage(pMS, pM, NULL);

	// check header
	recvID = (*(pM->pH))[eAPMsgHeaderPosition_receiver];
	if ((recvID == dAPNodeID_ALL) || (recvID == pAP->nodeID)) {
		APHandleMsg (pAP,pM);
	}
	// free memory
	APMSdeleteMsg (pAP->MS,pM);
}

// ===============================
// driver uuid =3
// ===============================

// struct for the drv data
typedef struct SMSP430uartDrv {
	TAPMsgHeader				h;			//!< transmit header template
	TAPReceiveStateMachine		rxSM;		//!< receive state machine
} TMSP430uartDrv;

TMSP430uartDrv gMSP430uartDrv;

// ------------------------------
// drv own functions
// ------------------------------

void drv_3_sendHeader (
		uint32_t msgId,
		uint32_t recv,
		uint32_t num,
		uint32_t length
	) {
	gMSP430uartDrv.h[eAPMsgHeaderPosition_msgTypeID] = msgId;
	gMSP430uartDrv.h[eAPMsgHeaderPosition_msgNumber] = num;
	gMSP430uartDrv.h[eAPMsgHeaderPosition_receiver] = recv;
	gMSP430uartDrv.h[eAPMsgHeaderPosition_length] = length;
	msp430_UART_send((unsigned char *)gMSP430uartDrv.h,sizeof(TAPMsgHeader));
}


// function for feeding the recv state machine
void drv_3_feedRecvStateM () {
	uint32_t d;
	while (!stjFIFO_readElement(&gMsp430_uartFIFO ,&d)) {
		gMSP430uartDrv.rxSM.state (&(gMSP430uartDrv.rxSM), &d, 1);
	}
}
//open an existing driver and bind it to the AP
int drv_3_open (void * pAP, struct SAPMsgDrv *pDrv) {
	// init header
	gMSP430uartDrv.h[eAPMsgHeaderPosition_endian] = ((TAP *) pAP)->sysEndian;
	gMSP430uartDrv.h[eAPMsgHeaderPosition_sender] = ((TAP *) pAP)->nodeID;
	gMSP430uartDrv.h[eAPMsgHeaderPosition_receiver] = dAPNodeID_ALL;
	gMSP430uartDrv.h[eAPMsgHeaderPosition_msgTypeID] = 0;
	gMSP430uartDrv.h[eAPMsgHeaderPosition_msgNumber] = 0;
	gMSP430uartDrv.h[eAPMsgHeaderPosition_length] = 0;

	// save driver data
	pDrv->pDrvData = &gMSP430uartDrv;

	// init receive state machine
	APInitReceiveStateMachine(
			&gMSP430uartDrv.rxSM,
			((TAP *)pAP)->MS,
			pDrv
		);
	return 0;
}

//close the driver
int drv_3_close (struct SAPMsgDrv *pDrv) {
	return 0;
}
//destroys the driver
int drv_3_destroy (struct SAPMsgDrv *pDrv) {
	return 0;
}


//send raw data
int drv_3_sendRaw (struct SAPMsgDrv *pDrv, int amount, uint32_t *pData) {
	msp430_UART_send((unsigned char *)pData,amount*sizeof(uint32_t));
	return 0;
}


//acknowledge
int drv_3_ACK (void *pDrvData, uint32_t receiver, uint32_t mNum) {
	drv_3_sendHeader(eAPMsgTypes_ACK, receiver, mNum, 0);
	return 0;
}
//not acknowledge
int drv_3_NACK (void *pDrvData, uint32_t receiver, uint32_t mNum) {
	drv_3_sendHeader(eAPMsgTypes_NACK, receiver, mNum, 0);
	return 0;
}
//start sending a program
int drv_3_startPrg (void *pDrvData, uint32_t receiver, uint32_t mNum, int32_t globalVariableNumber, int32_t localVariableNumber, int32_t instructionNumber) {
	drv_3_sendHeader(eAPMsgTypes_startPrg, receiver, mNum, 0);
	msp430_UART_send((unsigned char *)&globalVariableNumber,sizeof(int32_t));
	msp430_UART_send((unsigned char *)&localVariableNumber,sizeof(int32_t));
	msp430_UART_send((unsigned char *)&instructionNumber,sizeof(int32_t));
	return 0;
}

//sends a variable
int drv_3_sendVariable (void *pDrvData, uint32_t receiver, uint32_t mNum, int32_t index, int32_t varTypeID, int32_t num) {
	drv_3_sendHeader(eAPMsgTypes_sendVariable, receiver, mNum, 3);
	msp430_UART_send((unsigned char *)&index,sizeof(int32_t));
	msp430_UART_send((unsigned char *)&varTypeID,sizeof(int32_t));
	msp430_UART_send((unsigned char *)&num,sizeof(int32_t));
	return 0;
}
//sends an instruction
int drv_3_sendInstruction (void *pDrvData, uint32_t receiver, uint32_t mNum, int32_t index, uint32_t * fbc) {
	drv_3_sendHeader(eAPMsgTypes_sendInstruction, receiver, mNum, dAPInterpreterFuncMaxParams + 1);
	msp430_UART_send((unsigned char *)&index,sizeof(int32_t));
	msp430_UART_send((unsigned char *)fbc,sizeof(int32_t) * (dAPInterpreterFuncMaxParams+1));
	return 0;
}
//sings that the program transmission has completed
int drv_3_endPrg (void *pDrvData, uint32_t receiver, uint32_t mNum) {
	drv_3_sendHeader(eAPMsgTypes_endPrg, receiver, mNum, 0);
	return 0;
}
//stops the AP
int drv_3_stop (void *pDrvData, uint32_t receiver, uint32_t mNum) {
	drv_3_sendHeader(eAPMsgTypes_stop, receiver, mNum, 0);
	return 0;
}
//the AP executes one instruction
int drv_3_step (void *pDrvData, uint32_t receiver, uint32_t mNum) {
	drv_3_sendHeader(eAPMsgTypes_step, receiver, mNum, 0);
	return 0;
}
//the AP runs the program
int drv_3_run (void *pDrvData, uint32_t receiver, uint32_t mNum) {
	drv_3_sendHeader(eAPMsgTypes_run, receiver, mNum, 0);
	return 0;
}
//a variable going to be updated
int drv_3_updateVariable (void *pDrvData, uint32_t receiver, uint32_t mNum, int32_t gIndex, int32_t dataElements) {
	drv_3_sendHeader(eAPMsgTypes_updateVariable, receiver, mNum, dataElements + 1);
	msp430_UART_send((unsigned char *)&gIndex,sizeof(int32_t));
	return 0;
}
//a AP is going to be logged in to the system
int drv_3_login (void *pDrvData, uint32_t receiver, uint32_t mNum) {
	drv_3_sendHeader(eAPMsgTypes_login, receiver, mNum, 0);
	return 0;
}
//a AP is going to be logged out of the system
int drv_3_logout (void *pDrvData, uint32_t receiver, uint32_t mNum) {
	drv_3_sendHeader(eAPMsgTypes_logout, receiver, mNum, 0);
	return 0;
}

