	TAPgenericRationalVector* pb = (TAPgenericRationalVector *)pParams[2].fp_pD;
// parameter ib descr: b index
	int32_t ib = pParams[3].fp_integer;

	if (APgenericRationalVector_cmpEle(pa, pb, ia, ib)  < 0) {
		((TAPInterpreterCPU *)pIPcpu)->CF = 0;
	} else {
		((TAPInterpreterCPU *)pIPcpu)->CF = 1;
	}
	((TAPInterpreterCPU *)pIPcpu)->pIP++;
};

// ========================================
// dynamics processing (DynProc)
// ========================================
// Literature:
// Udo Zlzer, Digitale Audiosignal Verarbeitung, 3. Auflage
// Udo Zlzer, DAFX - Digital Audio Effects


// short:
// 		AT - attack time
//		RT - release time
//		TAV - average time
//		LT- limiter threshold
//		LS - limiter slope


// ---------------------------------
// helper
// ---------------------------------

float DynProc_calcSlope (
		float x1db, float y1db,
		float x2db, float y2db
	);

float DynProc_calcThreshold (
		float xDB,
		float xMax
	);

float DynProc_calcDB (float v);

float DynProc_calcValueFromDB (float dbVlaue, float xMax);

//! calc time parameter (AT, RT, TAV)
float DynProc_calcTimeParameter (
	float 		Ta, 	//!< (in) sampling period
	float		t		//!< (in) time parameter
	);

float DynProc_calcTimeFromTimeparameter (
		float Ta,
		float Tval
		);


// ---------------------------------
// RMS
// ---------------------------------
// RMS calc after Zlzer @p. 238

// structure for RMS calculation
typedef struct SDynProc_RMS {
	float	TAV;			//!< time average
	float	oneMinusTAV;	//!< 1-TAV
	float	x2Old;			//!< x(n-1)
} TDynProc_RMS;

//! init rms
void DynProc_InitRMS (
	TDynProc_RMS *	pRMS,	//!< (in/out) structure to be filled
	float			TAV		//!< (in) averaging time coefficient
	);

//! calc rms for one step (!rms = x!)
float DynProc_calcRMS (
		TDynProc_RMS *	pRMS,	//!< (in) structure to be filled
		float 			x		//!< (in) input signal witch should be rms
	);

// ---------------------------------
// Peak
// ---------------------------------
// Peak calc after Zlzer

// structure for Peak calculation
typedef struct SDynProc_Peak {
	float	AT;			//!< attack time
	float	RT;			//!< release time
	float	oneMinusAT;	//!< 1 - AT
	float	oneMinusRT;	//!< 1 - RT
	float	peak;		//!< peak
} TDynProc_Peak;


//! init peak
void DynProc_InitPeak (
	TDynProc_Peak *	pPeak,	//!< (in/out) structure to be filled
	float			AT,		//!< (in) attack time
	float			RT		//!< (in) release time
	);

//! calc peak for one step
float DynProc_calcPeak (
		TDynProc_Peak *	pPeak,	//!< (in) structure to be filled
		float 			x		//!< (in) input signal witch should be rms
	);


// ---------------------------------
// smooth gain
// ---------------------------------
// smooth the gain value
// idea: use a hysteresis curve
//
// formula: g(n) = (1-k) * g(n - 1) + k * f(n)
//
// k = AT or k = RT
// k = (f(n) > f(n-1)) ? AT : RT
// if new value is over the old value -> attack
// else -> release

// structure for attack and release time adjustment calculation
typedef struct SDynProc_SmoothG {
	float 			AT;		//!< attack time
	float 			RT;		//!< release time
	float			fOld;	//!< f(n-1)
	float			gOld;	//!< g(n-1)
} TDynProc_SmoothG;

//! init AT/RT
void DynProc_InitSmoothG (
	TDynProc_SmoothG *	pSG,	//!< (in/out) structure to be filled
	float				AT,		//!< (in) attack time
	float				RT		//!< (in) release time
	);

//! init AT/RT
float DynProc_calcSmoothG (
	float 				fn,		//!< (in) input
	TDynProc_SmoothG *	pSG	//!< (in) structure to be filled
	);

// ---------------------------------
// limiter
// ---------------------------------
// LT - limiter treshold (where the limiter starts working)
// LS - limiter slope (how fast the limiter works)

// structure for dynamic range controler calculation
typedef struct SDynProc_Limiter {
	float 				LTlog;			//!< limiter threshold LT
	float 				LS;				//!< limiter slope LS
	TDynProc_Peak		peakDetector;	//!< the peak detector
	TDynProc_SmoothG	SmoothG;		//!< AT/RT block
	float				delay;			//!< delay at the input - output lane
} TDynProc_Limiter;

//! init AT/RT
void DynProc_InitLimiter (
		TDynProc_Limiter *	pLim,		//!< (in/out) structure to be filled
		float				peakAT,		//!< (in) peak attack time
		float				peakRT,		//!< (in) peak release time
		float				smoothAT,	//!< (in) smoothing attack time
		float				smoothRT,	//!< (in) smoothing release time
		float				LT,			//!< (in) limiter threshold
		float				LS			//!< (in) limiter slope
);

//! init AT/RT
float DynProc_calcLimiter (
		TDynProc_Limiter *	pLim,	//!< (in) the limiter
		float 				x		//!< (in) input signal
	);

// ---------------------------------
// compressor
// ---------------------------------
// CT - compressor treshold (where the compressor starts working)
// CS - compressor slope (how fast the compressor works)

// structure for dynamic range controller calculation
typedef struct SDynProc_Compressor {
	float 				CTlog;				//!< compressor threshold
	float 				CSlog;				//!< compressor slope
	TDynProc_RMS		RMS;			//!< RMS unit
	TDynProc_SmoothG	SmoothG;		//!< AT/RT block
	float				delay[2];		//!< delay at the input - output lane
} TDynProc_Compressor;



//! init compressor
void DynProc_InitCompressor (
	TDynProc_Compressor *	pComp,		//!< (in/out) structure to be filled
	float					rmsTAV,		//!< (in) rms time average coefficient
	float					smoothAT,	//!< (in) smoothing attack time
	float					smoothRT,	//!< (in) smoothing release time
	float					CT,			//!< (in) compressor treshold
	float					CS			//!< (in) compressor slope
);

//! calc compressor
float DynProc_calcCompressor (
		TDynProc_Compressor *	pComp,	//!< (in) the compressor
		float 					x		//!< (in) input signal
	);

// ---------------------------------
// expander
// ---------------------------------
// ET - expander threshold (where the expander starts working)
// ES - expander slope (how fast the expander works)

// structure for dynamic range controller calculation
typedef struct SDynProc_Expander {
	float 				ETlog;		//!< expander threshold log10 (ET)
	float 				ESlog;		//!< expander slope log10 (ES)
	TDynProc_RMS		RMS;			//!< RMS unit
	TDynProc_SmoothG	SmoothG;		//!< AT/RT block
	float				delay[2];		//!< delay at the input - output lane
} TDynProc_Expander;

//! init expander
void DynProc_InitExpander (
	TDynProc_Expander *	pExp,		//!< (in/out) structure to be filled
	float				rmsTAV,		//!< (in) rms time average coefficient
	float				smoothAT,	//!< (in) smoothing attack time
	float				smoothRT,	//!< (in) smoothing release time
	float				ET,			//!< (in) expander treshold in dB
	float				ES			//!< (in) expander slope in dB
);

//! calc expander
float DynProc_calcExpander (
		TDynProc_Expander *	pExp,	//!< (in) the expander
		float 					x		//!< (in) input signal
	);

// ---------------------------------
// noisegate
// ---------------------------------
// NT - noisegate threshold (till the noisegate works)

// structure for dynamic range controller calculation
typedef struct SDynProc_Noisegate {
	float 				NTlog;			//!< noisegate threshold log10 (NT)
	float 				NSlog;			//!< noisegate slope log10 (NS)
	TDynProc_RMS		RMS;			//!< RMS unit
	TDynProc_SmoothG	SmoothG;		//!< AT/RT block
	float				delay[2];		//!< delay at the input - output lane
} TDynProc_Noisegate;

//! init noisegate
void DynProc_InitNoisegate (
	TDynProc_Noisegate *	pNG,		//!< (in/out) structure to be filled
	float					rmsTAV,		//!< (in) rms time average coefficient
	float					smoothAT,	//!< (in) smoothing attack time
	float					smoothRT,	//!< (in) smoothing release time
	float					NT,	//!< (in) noisegate threshold
	float					NS	//!< (in) noisegate slope
);

//! calc noisegate
float DynProc_calcNoisegate (
		TDynProc_Noisegate *	pNG,	//!< (in) the noisegate
		float 					x		//!< (in) input signal
	);

// ========================================
// dynamics processing (DynProc)
// ========================================
// Literature:
// Udo Zlzer, Digitale Audiosignal Verarbeitung, 3. Auflage
// Udo Zlzer, DAFX - Digital Audio Effects


// short:
// 		AT - attack time
//		RT - release time
//		TAV - average time
//		LT- limiter threshold
//		LS - limiter slope

// it works in that way
// Y(n) = G(n) * x(n -D)
// g = {noisegate, expander, compressor, limiter}
// D - delay m - samples


// helper

float DynProc_calcSlope (
		float x1db, float y1db,
		float x2db, float y2db
	) {
	// y = m * x + n; P1 & P2
	// m = (P2y - P1y) / (P2x - P1x)
	return (y2db - y1db) / (x2db - x1db);
}

float DynProc_calcThreshold (
		float xDB,
		float xMax
	) {
	return xMax * powf (10.0f, xDB / 20.0f);
}

float DynProc_calcDB (float x) {
	return 10.0f * logf(x*x); // = 10.0 * log(x)  = 20.0f * log(x)
}

float DynProc_calcValueFromDB (float dbVlaue, float xMax) {
	return powf(10.0f, dbVlaue / 20.0) * xMax;
}


float DynProc_calcCompressionFactor (float slope) {
	return 1.f / (1.f- slope);
}

//! calc time parameter (AT, RT, TAV)
float DynProc_calcTimeParameter (
	float 		Ta, 	//!< (in) sampling period
	float		t		//!< (in) time parameter
	) {
	// formular and 2.2 explained at "Digitale Audio Signalverarbeitung" @p.237
	return (1.0f - expf((-2.2f * Ta) / t));
}

float DynProc_calcTimeFromTimeparameter (
		float Ta,
		float Tval
		) {
	return (-2.2f * Ta)/ (logf(1-Tval));
}

// ---------------------------------
// RMS
// ---------------------------------
// RMS calc after Zlzer @p. 238

//! init rms
void DynProc_InitRMS (
	TDynProc_RMS *	pRMS,	//!< (in/out) structure to be filled
	float			TAV		//!< (in) averaging time coefficient
	) {
	pRMS->TAV = TAV;
	pRMS->oneMinusTAV = 1.0f - TAV;
	pRMS->x2Old = 0.0f;
}

//! calc rms for one step
float DynProc_calcRMS (
		TDynProc_RMS *	pRMS,	//!< (in) structure to be filled
		float 			x		//!< (in) input signal witch should be rms
	) {
	float x2RMS;
	// calc forward
	x2RMS = pRMS->oneMinusTAV * pRMS->x2Old + pRMS->TAV * x * x;
	// calc backward
	pRMS->x2Old = x2RMS;
	return x2RMS;
}

// ---------------------------------
// Peak
// ---------------------------------
// Peak calc after Zlzer

//! init peak
void DynProc_InitPeak (
	TDynProc_Peak *	pPeak,	//!< (in/out) structure to be filled
	float			AT,		//!< (in) attack time
	float			RT		//!< (in) release time
	) {
	pPeak->AT = AT;
	pPeak->RT = RT;
	pPeak->oneMinusAT = 1.0f - AT;
	pPeak->oneMinusRT = 1.0f - RT;
	pPeak->peak = 0.0f;
}

//! calc peak for one step
float DynProc_calcPeak (
		TDynProc_Peak *	pPeak,	//!< (in) structure to be filled
		float 			x		//!< (in) input signal witch should be rms
	) {

	// formula from Zlzer @p. 235 wrong!
	// |x(n)| > xPeak(n-1) -> attack
	// |x(n)| <= xPeak(n-1) -> release
	// @attack: xpeak(n) = (1-AT) * xpeak(n-1) + AT * |x(n)|
	// @release: xpeak(n) = (1-RT) * peak(n-1)
	float a;
	a = fabsf(x);

	if (a > pPeak->peak) {
		pPeak->peak = pPeak->oneMinusAT * (pPeak->peak) + pPeak->AT * a;
	} else {
		pPeak->peak = pPeak->oneMinusRT * (pPeak->peak) + pPeak->RT * a;
	}
	return pPeak->peak;
}

// ---------------------------------
// smooth gain
// ---------------------------------
// smooth the gain value
// idea: use a hysteresis curve
//
// formula: g(n) = (1-k) * g(n - 1) + k * f(n)
//
// k = AT or k = RT
// k = (f(n) > f(n-1)) ? AT : RT
// if new value is over the old value -> attack
// else -> release

//! init AT/RT
void DynProc_InitSmoothG (
	TDynProc_SmoothG *	pSG,	//!< (in/out) structure to be filled
	float				AT,		//!< (in) attack time
	float				RT		//!< (in) release time
	) {
	pSG->AT = AT;
	pSG->RT = RT;
	pSG->fOld = 0.0f;
	pSG->gOld = 0.0f;
}

//! init AT/RT
float DynProc_calcSmoothG (
	float 				fn,		//!< (in) input
	TDynProc_SmoothG *	pSG	//!< (in) structure to be filled
	) {
	float k;
	float gn;

	// attack or release
	k = (fn > pSG->fOld) ? pSG->AT : pSG->RT;

	// calc gain
	gn = (1.0f-k) * pSG->gOld + k * fn;
	pSG->gOld = gn;
	pSG->fOld = fn;
	return gn;
}

// ---------------------------------
// limiter
// ---------------------------------
// LT - limiter treshold (where the limiter starts working)
// LS - limiter slope (how fast the limiter works)

//! init AT/RT
void DynProc_InitLimiter (
	TDynProc_Limiter *	pLim,		//!< (in/out) structure to be filled
	float				peakAT,		//!< (in) peak attack time
	float				peakRT,		//!< (in) peak release time
	float				smoothAT,	//!< (in) smoothing attack time
	float				smoothRT,	//!< (in) smoothing release time
	float				LT,			//!< (in) limiter threshold
	float				LS			//!< (in) limiter slope
) {
	DynProc_InitPeak(&(pLim->peakDetector),peakAT,peakRT);
	DynProc_InitSmoothG(&(pLim->SmoothG),smoothAT,smoothRT);
	pLim->LTlog = log10f(LT);
	pLim->LS = LS;
	pLim->delay = 0.0f;
}

//! init AT/RT
float DynProc_calcLimiter (
		TDynProc_Limiter *	pLim,	//!< (in) the limiter
		float 				x		//!< (in) input signal
	) {
	float y;
	float g;

	// 1.) calc direct feedthrough lane
	y = pLim->delay;
	// 1.1.) recalc delay
	pLim->delay = x;

	// 2.) calc limiter gain
	// 2.1) peak detect
	g = DynProc_calcPeak(&(pLim->peakDetector),x);
	g = log10f(g);
	// 2.3. if we are over the threshold
	if (g > pLim->LTlog) {
		g = -pLim->LS * (g - pLim->LTlog);
	} else {
		g = 0.0;
	}
	g = powf (10.0f, g);
	g = DynProc_calcSmoothG(g, &(pLim->SmoothG));
	// 3. calc result of delay mul compressor gain
	y *= g;

	// AND return
	return y;
}

// ---------------------------------
// compressor
// ---------------------------------
// CT - compressor treshold (where the compressor starts working)
// CS - compressor slope (how fast the compressor works)

//! init compressor
void DynProc_InitCompressor (
	TDynProc_Compressor *	pComp,		//!< (in/out) structure to be filled
	float					rmsTAV,		//!< (in) rms time average coefficient
	float					smoothAT,	//!< (in) smoothing attack time
	float					smoothRT,	//!< (in) smoothing release time
	float					CT,			//!< (in) compressor treshold
	float					CS			//!< (in) compressor slope
) {
	DynProc_InitRMS(&(pComp->RMS),rmsTAV);
	DynProc_InitSmoothG(&(pComp->SmoothG),smoothAT,smoothRT);
	pComp->CTlog = DynProc_calcDB(CT);
	pComp->CSlog = -CS;
	pComp->delay[0] = 0.0f;
	pComp->delay[1] = 0.0f;
}

//! calc compressor
float DynProc_calcCompressor (
		TDynProc_Compressor *	pComp,	//!< (in) the compressor
		float 					x		//!< (in) input signal
	) {
	float y;
	float g;

	// 1.) calc direct feedthrough lane
	y = pComp->delay[1];
	// 1.1.) recalc delay
	pComp->delay[1] = pComp->delay[0];
	pComp->delay[0] = x;

	// 2.) calc compressor gain
	// 2.1) peak detect
	g = DynProc_calcRMS(&(pComp->RMS),x);
	// 2.2) lin/log
	g = log10f(g);
	g *= 10.0f; // rms = x - > log(x) = 2 log(x) -> Xdb = 20*log(x) -> 20 / 2 = 10

	// 2.3. if we are over the threshold
	if (g > pComp->CTlog) {
		g = pComp->CSlog*(g-pComp->CTlog) / 20.0f;
	} else {
		g = 0.0f;
	}
	g = powf (10.0f, g);
	g = DynProc_calcSmoothG(g, &(pComp->SmoothG));
	// 3. calc result of delay mul compressor gain
	y *= g;

	// AND return
	return y;
}


// ---------------------------------
// expander
// ---------------------------------
// ET - expander threshold (where the expander starts working)
// ES - expander slope (how fast the expander works)

//! init expander
void DynProc_InitExpander (
	TDynProc_Expander *	pExp,		//!< (in/out) structure to be filled
	float				rmsTAV,		//!< (in) rms time average coefficient
	float				smoothAT,	//!< (in) smoothing attack time
	float				smoothRT,	//!< (in) smoothing release time
	float				ET,			//!< (in) expander treshold in dB
	float				ES			//!< (in) expander slope in dB
) {
	DynProc_InitRMS(&(pExp->RMS),rmsTAV);
	DynProc_InitSmoothG(&(pExp->SmoothG),smoothAT,smoothRT);
	pExp->ETlog = DynProc_calcDB(ET);
	pExp->ESlog = ES;
	pExp->delay[0] = 0.0f;
	pExp->delay[1] = 0.0f;
}

//! calc expander
float DynProc_calcExpander (
		TDynProc_Expander *		pExp,	//!< (in) the expander
		float 					x		//!< (in) input signal
	) {
	float y;
	float g;

	// 1.) calc direct feedthrough lane
	y = pExp->delay[1];
	// 1.1.) recalc delay
	pExp->delay[1] = pExp->delay[0];
	pExp->delay[0] = x;

	// 2.) calc compressor gain
	// 2.1) peak detect
	g = DynProc_calcRMS(&(pExp->RMS),x);
	// 2.2) lin/log
	g = log10f(g);
	g *= 10.0f; // rms = x - > log(x) = 2 log(x) -> Xdb = 20*log(x) -> 20 / 2 = 10

	// 2.3. if we are over the threshold
	if (g > pExp->ETlog) {
		g = pExp->ESlog*(pExp->ETlog-g) / 20.0;
	} else {
		g = 0.0f;
	}
	g = powf (10.0f, g);
	g = DynProc_calcSmoothG(g, &(pExp->SmoothG));
	// 3. calc result of delay mul compressor gain
	y *= g;

	// AND return
	return y;
}


// ---------------------------------
// noisegate
// ---------------------------------
// NT - noisegate threshold (till the noisegate works)

//! init noisegate
void DynProc_InitNoisegate (
	TDynProc_Noisegate *	pNG,		//!< (in/out) structure to be filled
	float					rmsTAV,		//!< (in) rms time average coefficient
	float					smoothAT,	//!< (in) smoothing attack time
	float					smoothRT,	//!< (in) smoothing release time
	float					NT,			//!< (in) noisegate threshold
	float					NS			//!< (in) noisegate slope
) {
	DynProc_InitRMS(&(pNG->RMS),rmsTAV);
	DynProc_InitSmoothG(&(pNG->SmoothG),smoothAT,smoothRT);
	pNG->NTlog = DynProc_calcDB(NT);
	pNG->NSlog = NS;
	pNG->delay[0] = 0.0f;
	pNG->delay[1] = 0.0f;
}

//! calc noisegate
float DynProc_calcNoisegate (
		TDynProc_Noisegate *	pNG,	//!< (in) the noisegate
		float 					x		//!< (in) input signal
	) {
	float y;
	float g;

	// 1.) calc direct feedthrough lane
	y = pNG->delay[1];
	// 1.1.) recalc delay
	pNG->delay[1] = pNG->delay[0];
	pNG->delay[0] = x;

	// 2.) calc noisegate gain
	// 2.1) rms detect
	g = DynProc_calcRMS(&(pNG->RMS), x);
	// 2.2) lin/log
	g = log10f(g);
	g *= 10.0f; // rms = x - > log(x) = 2 log(x) -> Xdb = 20*log(x) -> 20 / 2 = 10

	// 2.3. if we are over the threshold
	if (g > pNG->NTlog) {
		// calc linear function at log space
		g = pNG->NSlog*(g-pNG->NTlog) / 20.0;
		g = powf(10.f, g);
	} else {
		g = 0.0f;
	}
	// and smooth
	g = DynProc_calcSmoothG(g, &(pNG->SmoothG));
	// 3. calc result of delay mul noisegate gain
	y *= g;

	// AND return
	return y;
}

