// variable implementation for a integer number(var id = 3)
// ===============================

// --------------------------------------
// TAPstring
// --------------------------------------

// AP string type
typedef struct SAPstring {
	char * 		szTxt;	//!< pointer to the vector of chars
	int 		len;	//!< length of the string
} TAPstring;

// creates a AP string
int APstring_create (
		TAPstring * 	pStr,	//!<(in) pointer to the string structure witch should be filled
		int 			len		//!<(in) length of the string
	) {
	(pStr->szTxt) = malloc ((len+1)*sizeof(char));
	if (!pStr->szTxt) return -1;
	*(pStr->szTxt) = 0;
	pStr->len = len;
	return 0;
}

// frees a AP string
void APstring_free (
		TAPstring * 	pStr	//!<(in) pointer to the string
	) {
	free ((pStr->szTxt));
	pStr->szTxt = NULL;
	pStr->len = 0;
}

// resizes the string
int APstring_resize (
		TAPstring * 	pStr,	//!<(in) pointer to the string
		int				newLen	//!<(in) new length of the string
	) {
	char * pNS = realloc(pStr->szTxt,(newLen+1));
	if (!pNS) return -1;
	pStr->szTxt = pNS;
	pStr->len = newLen;
	return 0;
}

// fills the string from an extern source
void APstring_fill(
		TAPstring * 	pStr,	//!<(in) pointer to the string
		int				iStart,	//!<(in) start index at the string
		int				imax,	//!<(in) end index of the string
		char *			pSource	//!<(in) source from where the chars are copied
	) {
	int i;
	char * pDest = pStr->szTxt +  iStart;
	// clip str
	if (imax > pStr->len) imax = pStr->len - iStart;

	for (i = 0;i < imax; i++) {
		*pDest = *pSource;
		pDest++;
		pSource++;
	}
	*pDest = 0;
}

// --------------------------------------
// TAPstringVector
// --------------------------------------

// vector of strings type
typedef struct SAPstringVector {
	TAPstring *		pStr;	//!< pointer to the first string at the vector
	unsigned int	num;	//!< amount of strings at the vector
} TAPstringVector;

// creates a AP string
TAPstringVector * APstringVector_create (
		int		num		//!<(in) number of strings at the vector
	) {
	TAPstringVector * pR;
	int i;

	pR = malloc(sizeof(TAPstringVector));
	if (!pR) return NULL;
	pR->pStr = malloc(sizeof(TAPstring)*num);
	if (!pR->pStr) {
		free (pR);
		return NULL;
	}
	// init str
	TAPstring * pS = pR->pStr;
	for (i = 0; i < num; i++) {
		pS->szTxt = NULL;
		pS->len = 0;
		pS++;
	}
	pR->num = num;
	return pR;
}

// frees a AP string
void APstringVector_free (
		TAPstringVector * 	pSV	//!<(in) pointer to the string vector
	) {
	unsigned int i;
	TAPstring * pS = pSV->pStr;

	for (i = 0; i < pSV->num; i++) {
		APstring_free(pS);
		pS++;
	}
	free(pSV->pStr);
	free(pSV);
}

