int APInterpreterLoadVar (TAPInterpreter IP, int32_t index, int32_t varTypeID)
{

}

// load a single Instruction to an index
int APInterpreterLoadInstr (TAPInterpreter IP, int32_t i, int32_t * pRawInstr)
{

}

