// ==============================
// descr: some helpers for the lib-snd-file group
// author: Stefan Jaritz
//
// the lib is the "libsndfile-1"
// ==============================

typedef struct SStjWAVOpenInfo {
	unsigned int	channelNumber;			//!< logical channel
	char *			szFileName;				//!< name of the file
	unsigned int	isInput;				//!< if <>0 then this file is an input
	unsigned int	sampleRate;				//!< the sample rate
	unsigned int	fileChannelNumber;		//!< number of the channel at the file (starting at 1)
	unsigned int	sampleAmountCacheSize;	//!< amount of samples cached before writing or/ at reading
} TStjWAVOpenInfo;

typedef struct SStjWAVFile {
	unsigned int	channel;			//!< the channel
	SNDFILE *		pSndF;				//!< the file
	unsigned int	isInput;			//!< if <> 0 the channel is an input channel
	unsigned int	channelsAmount; 	//!< the amount of channels
	unsigned int	sampleAmount;		//!< the amount of samples at the cache
	unsigned int	maxSampleAmount;	//!< the maximum sample amount
	unsigned int	dataOffset;			//!< the offset of the channel at the sample cache
	float *			pCache;				//!< the sample cache
	float *			pD;					//!< pointer to the data
} TStjWAVFile;


typedef struct SStjWAVmodule {
	TStjWAVFile *	pChannels;
	unsigned int	number;
} TStjWAVmodule;

// fill the wav sample cache
int WAVmoduleFillCache (TStjWAVFile * pWF) {
	sf_count_t amount = (sf_count_t) pWF->maxSampleAmount;
	// read a data frame(s)
	amount = sf_readf_float (pWF->pSndF,pWF->pCache, amount);
	if (amount < 1) return -1;
	// reset data pointers and indexes
	pWF->pD = pWF->pCache;
	pWF->sampleAmount = (unsigned int) amount;
	return 0;
}

// flushes the wav sample cache to disk
int WAVmoduleFlushCache (TStjWAVFile * pWF) {
	if (!pWF->sampleAmount) return 0;
	sf_count_t amount = (sf_count_t) pWF->sampleAmount;
	if (amount != sf_write_float (pWF->pSndF,pWF->pCache , amount)) return -1;
	pWF->pD = pWF->pCache;
	pWF->sampleAmount = 0;
	return 0;
}


//! close the set of wav files
void WAVmoduleExit (TStjWAVmodule * pM) {
	//1. close all sound files
	int i;

	TStjWAVFile * pWF = pM->pChannels;
	for (i = 0; i < pM->number;i++) {
		// if it is an output flush the samples to disk
		if (!pWF->isInput) {
			WAVmoduleFlushCache(pWF);
		}
		sf_close(pWF->pSndF);
		if (pWF->pCache) {
			free (pWF->pCache);
		}
		pWF++;
	}
	//2. free array
	free (pM->pChannels);

	//3. set all vars of the struct to default
	pM->number = 0;
	pM->pChannels = NULL;
}


//! initis a set of wav files for writing / reading
int WAVmoduleInit (
		int 				number,		//!<(in) amount of files
		TStjWAVOpenInfo *	pWAVFiles,	//!<(in) file description
		TStjWAVmodule *		pModul		//!<(in/out) modul descriptor witch is filled
	) {

	pModul->pChannels = malloc (sizeof(TStjWAVFile) * number);
	if (!pModul->pChannels) {
		return -1;
	}
	memset (pModul->pChannels,0,sizeof(TStjWAVFile) * number);
	pModul->number = number;

	int 			i;
	SF_INFO 		info;
	TStjWAVFile * 	pWF = pModul->pChannels;
	size_t			cacheSize;

	for (i = 0;i < number;i++) {
		memset (&info,0,sizeof(SF_INFO));
		if (pWAVFiles[i].isInput) {
			pWF->pSndF = sf_open (pWAVFiles[i].szFileName,SFM_READ, &info) ;
		} else {
			info.samplerate = pWAVFiles[i].sampleRate;
			info.channels = 1;
			info.format = SF_FORMAT_WAV | SF_FORMAT_FLOAT;
			pWF->pSndF = sf_open (pWAVFiles[i].szFileName,SFM_WRITE, &info) ;
		}
		if (!pWF->pSndF) {
			goto error;
		}
		pWF->isInput = (unsigned int) pWAVFiles[i].isInput;
		pWF->channel = (unsigned int) pWAVFiles[i].channelNumber;
		pWF->channelsAmount = (unsigned int) info.channels;
		pWF->dataOffset = (unsigned int) pWAVFiles[i].fileChannelNumber;
		pWF->maxSampleAmount = (unsigned int) pWAVFiles[i].sampleAmountCacheSize;
		cacheSize =  pWF->channelsAmount * pWF->maxSampleAmount * sizeof(float);
		pWF->pCache = malloc (cacheSize);
		if (!pWF->pCache) goto error;
		memset (pWF->pCache,0,cacheSize);
		pWF->sampleAmount = 0;
		// now fill the cache with data
		if (pWF->isInput) {
			if (WAVmoduleFillCache(pWF)) goto error;
		} else {
			pWF->pD = pWF->pCache;
		}
		pWF++;
	}
	return 0;
error:
	WAVmoduleExit(pModul);
	return -2;
}

//! looking for the right channel
TStjWAVFile * WAVmoduleFindChannel (
		TStjWAVmodule *	pM,		//!< the module
		int				channel	//!< channel to search for
		) {
	TStjWAVFile * pWF = pM->pChannels;
	int i;
	for (i = 0; i < pM->number;i++) {
		if (pWF->channel == channel) return pWF;
		pWF++;
	}
	return NULL;
}

//! reads a sample from a channel
int WAVmoduleGetInput (
		TStjWAVmodule *	pM,			//!<(in) the module
		int				channel,	//!<(in) the channel
		float *			pSample		//!<(out) pointer to the sample buffer witch is filled
	) {
	TStjWAVFile * pWF = WAVmoduleFindChannel(pM,channel);
	// channel not found
	if (!pWF) {
		return -1;
	}
	// channel is not an input
	if (!pWF->isInput) {
		return -2;
	}
	// if the cache is empty then fill it
	if (!pWF->sampleAmount) {
		if (WAVmoduleFillCache(pWF)) return -3;
	}
	// copy sample
	*pSample = pWF->pD[pWF->dataOffset];
	pWF->pD += pWF->channelsAmount;
	// now there is one sample less at the buffer
	pWF->sampleAmount--;
	return 0;
}

//! writes a sample to an output
int WAVmoduleSetOutput (
		TStjWAVmodule *	pM,			//!<(in) the module
		int				channel,	//!<(in) the channel
		float 			sample		//!<(in) pointer to the sample buffer witch is filled
	) {
	TStjWAVFile * pWF = WAVmoduleFindChannel(pM,channel);
	// channel not found
	if (!pWF) {
		return -1;
	}
	// channel is not an output
	if (pWF->isInput) {
		return -2;
	}
	// if the cache is full then flush it
	if (pWF->sampleAmount == pWF->maxSampleAmount) {
		if (WAVmoduleFlushCache(pWF)) return -3;
	}
	// now store the value
	pWF->pD[pWF->dataOffset] = sample;
	pWF->pD += pWF->channelsAmount;
	pWF->sampleAmount++;
	return 0;
}

