#ifdef __cplusplus
using namespace std;
#endif

#include <stdio.h>
#include <stdlib.h>

#include "AP.h"

int main() {
	TAP AP;

	APinit(&AP,815,NULL,0,1000,eAP_littleEndian);


	int32_t varC;
	int32_t instrC;

	int32_t * varL;
	int32_t * instrL;
	int endian;
/*
	int res = loadALCfromFile ("../APgen/ALASM/test.alc",&endian,&varC,&instrC,&varL,&instrL);
	if (res) return EXIT_FAILURE;

	res = loadProgrammToTheAP (AP.IP,endian,varC,instrC,varL,instrL);
*/

	free (varL);
	free (instrL);

	APdelete(&AP);

	return EXIT_SUCCESS;
}

