enum eAdminMsgTypes {
	eAdminMsgType_login 	= 'i',
	eAdminMsgType_logout	= 'o',
	eAdminMsgType_exit		= 'e',
	eAdminMsgType_ack		= 'a',
	eAdminMsgType_nack		= 'n'
};


