	dataAmount = (int32_t) 1 + pV->num * 6;
	
	    
	// 1 send header
	pMD->pfkt_updateVariable(pMD->pDrvData, receiver, mNum, i, dataAmount);
	        
	// 2 send vector elementwise
	
	// 2.1 send amount of elements at the vector
	pMD->pfkt_sendInteger32(pMD,1,&pV->num);
	
	// 2.2 send element
	pE = pV->pFE;
	for (n = 0; n < pV->num; n++) {
	    pMD->pfkt_sendFloat32(pMD,1,&pE->n0);
		pMD->pfkt_sendFloat32(pMD,1,&pE->n1);
		pMD->pfkt_sendFloat32(pMD,1,&pE->n2);
		pMD->pfkt_sendFloat32(pMD,1,&pE->d0);
		pMD->pfkt_sendFloat32(pMD,1,&pE->d1);
		pMD->pfkt_sendFloat32(pMD,1,&pE->d2);
	    // inc
	    pE++;
	}
	return 0; 
}
