	rc = APinit(&AP,815,NULL,0,1000,eAP_littleEndian);
	if (rc) {
		printf("audio processor init failed with error code %i!",rc);
		goto exit;
	}


#ifdef dAPuseFileIO
	int32_t varC;
	int32_t instrC;

	int32_t * varL;
	int32_t * instrL;
