	rc = loadALCfromFile ("../APgen/ALASM/crossOver5Way.alc",&endian,&varC,&instrC,&varL,&instrL);
	if (rc) goto exit;
	rc = loadProgrammToTheAP (AP.IP,endian,varC,instrC,varL,instrL);
#endif

